libs/workflows/test/advanced-click-success-confirmation-test.json:36:        "script": "console.log('🔧 设置AdvancedClickNode点击成功确认测试环境...');\\n\\n// 获取当前页面信息\\nconst currentPage = {\\n  url: window.location.href,\\n  title: document.title\\n};\\n\\nconsole.log('当前页面:', currentPage);\\n\\n// 查找可测试的元素\\nconst testElements = {\\n  searchBox: {\\n    selector: '#kw',\\n    element: document.querySelector('#kw'),\\n    description: '百度搜索框',\\n    type: 'input'\\n  },\\n  searchButton: {\\n    selector: '#su',\\n    element: document.querySelector('#su'),\\n    element: document.querySelector('#su'),\\n    description: '百度搜索按钮',\\n    type: 'button'\\n  },\\n  newsLink: {\\n    selector: 'a[href*=\\\"news.baidu.com\\\"]',\\n    element: document.querySelector('a[href*=\\\"news.baidu.com\\\"]'),\\n    description: '百度新闻链接',\\n    type: 'navigation'\\n  },\\n  moreLink: {\\n    selector: 'a[href*=\\\"www.baidu.com/more\\\"]',\\n    element: document.querySelector('a[href*=\\\"www.baidu.com/more\\\"]'),\\n    description: '更多产品链接',\\n    type: 'navigation'\\n  },\\n  settingsLink: {\\n    selector: '#s-usersetting-top',\\n    element: document.querySelector('#s-usersetting-top'),\\n    description: '设置链接',\\n    type: 'settings'\\n  }\\n};\\n\\n// 过滤可见元素\\nconst visibleElements = [];\\nfor (const [name, info] of Object.entries(testElements)) {\\n  if (info.element && info.element.offsetParent !== null) {\\n    visibleElements.push({\\n      name: name,\\n      selector: info.selector,\\n      description: info.description,\\n      type: info.type,\\n      element: info.element,\\n      text: info.element.textContent?.trim() || info.element.value || '',\\n      href: info.element.href || ''\\n    });\\n    \\n    // 高亮显示找到的元素\\n    const originalStyle = info.element.style.cssText;\\n    info.element.style.border = '2px solid #4CAF50';\\n    info.element.style.backgroundColor = 'rgba(76, 175, 80, 0.2)';\\n    info.element.style.boxShadow = '0 0 8px rgba(76, 175, 80, 0.6)';\\n    \\n    // 在元素旁边添加标签\\n    const label = document.createElement('div');\\n    label.textContent = name;\\n    label.style.position = 'absolute';\\n    label.style.background = '#4CAF50';\\n    label.style.color = 'white';\\n    label.style.padding = '2px 6px';\\n    label.style.borderRadius = '3px';\\n    label.style.fontSize = '12px';\\n    label.style.zIndex = '9999';\\n    label.style.pointerEvents = 'none';\\n    \\n    const rect = info.element.getBoundingClientRect();\\n    label.style.top = (rect.top + window.scrollY - 25) + 'px';\\n    label.style.left = (rect.left + window.scrollX) + 'px';\\n    \\n    document.body.appendChild(label);\\n    \\n    setTimeout(() => {\\n      info.element.style.cssText = originalStyle;\\n      document.body.removeChild(label);\\n    }, 5000);\\n    \\n    console.log(`✅ 找到可测试元素: ${name} (${info.description})`);\\n    console.log(`  选择器: ${info.selector}`);\\n    console.log(`  文本内容: ${info.element.textContent?.trim() || info.element.value || ''}`);\\n    console.log(`  链接地址: ${info.element.href || ''}`);\\n  } else {\\n    console.log(`❌ 元素不可见: ${name}`);\\n  }\\n}\\n\\nif (visibleElements.length === 0) {\\n  console.error('❌ 未找到任何可测试的元素');\\n  return {\\n    success: false,\\n    error: '未找到任何可测试的元素',\\n    testElements: testElements,\\n    timestamp: new Date().toISOString()\\n  };\\n}\\n\\n// 选择最合适的测试元素（优先选择导航链接）\\nconst prioritizedElements = visibleElements.filter(el => el.type === 'navigation');\\nconst testElement = prioritizedElements.length > 0 ? prioritizedElements[0] : visibleElements[0];\\n\\nconsole.log(`🎯 选择测试元素: ${testElement.name}`);\\nconsole.log(`🎯 测试元素类型: ${testElement.type}`);\\nconsole.log(`🎯 测试元素描述: ${testElement.description}`);\\n\\n// 设置网络请求监听\\nconst networkRequests = [];\\nconst originalFetch = window.fetch;\\nwindow.fetch = function(...args) {\\n  const url = args[0];\\n  networkRequests.push({\\n    url: url,\\n    method: args[1]?.method || 'GET',\\n    timestamp: Date.now()\\n  });\\n  console.log('🌐 网络请求:', url);\\n  return originalFetch.apply(this, args);\\n};\\n\\n// 设置DOM变化监听\\nconst domChanges = [];\\nconst observer = new MutationObserver((mutations) => {\\n  mutations.forEach((mutation) => {\\n    if (mutation.type === 'childList' && mutation.addedNodes.length > 0) {\\n      domChanges.push({\\n        type: 'nodesAdded',\\n        count: mutation.addedNodes.length,\\n        timestamp: Date.now()\\n      });\\n      console.log('🔄 DOM变化: 新增节点', mutation.addedNodes.length);\\n    } else if (mutation.type === 'attributes') {\\n      domChanges.push({\\n        type: 'attributeChanged',\\n        attribute: mutation.attributeName,\\n        timestamp: Date.now()\\n      });\\n      console.log('🔄 DOM变化: 属性变化', mutation.attributeName);\\n    }\\n  });\\n});\\n\\nobserver.observe(document.body, {\\n  childList: true,\\n  subtree: true,\\n  attributes: true\\n});\\n\\nreturn {\\n  success: true,\\n  action: 'test_environment_setup',\\n  \\n  currentPage: currentPage,\\n  testElement: testElement,\\n  allElements: visibleElements,\\n  totalElements: visibleElements.length,\\n  \\n  testSetup: {\\n    networkMonitoring: true,\\n    domMonitoring: true,\\n    urlMonitoring: true,\\n    timestamp: Date.now()\\n  },\\n  \\n  timestamp: new Date().toISOString()\\n};",
