    const port = Number(this.options.port || 8765);
    this.wss = new WebSocketServer({ host, port });
    this.wss.on('connection', (socket) => {
      socket.on('message', (data) => this.handleMessage(socket, data));
    });
    this.wss.on('listening', () => {
