apps/webauto/platforms/alibaba/analysis/1688-advanced-chat-probe.json:86:        "message": "请运行以下JavaScript代码进行深度探测：\n\n1️⃣ Shadow DOM探测：\n// 查找所有包含Shadow DOM的元素\ndocument.querySelectorAll('*').forEach(el => {\n  if (el.shadowRoot) {\n    console.log('Shadow DOM元素:', el);\n    console.log('Shadow内容:', el.shadowRoot.innerHTML);\n  }\n});\n\n2️⃣ iframe内容探测：\n// 查看所有iframe内容\ndocument.querySelectorAll('iframe').forEach((frame, index) => {\n  try {\n    const doc = frame.contentDocument || frame.contentWindow.document;\n    console.log(`iframe ${index}内容:`, doc.body.innerHTML);\n  } catch(e) {\n    console.log(`iframe ${index}跨域:`, e.message);\n  }\n});\n\n3️⃣ 深度元素扫描：\n// 扫描所有可能的可编辑元素\nfunction scanEditableElements(root = document) {\n  const elements = [];\n  const walker = document.createTreeWalker(\n    root,\n    NodeFilter.SHOW_ELEMENT,\n    {\n      acceptNode: (node) => {\n        const tag = node.tagName.toLowerCase();\n        const editable = node.contentEditable === 'true' || \n                       node.isContentEditable ||\n                       node.getAttribute('contenteditable') === 'true';\n        const hasInputAttrs = node.hasAttribute('data-input') ||\n                           node.hasAttribute('role') && node.getAttribute('role') === 'textbox';\n        \n        if (editable || hasInputAttrs || \n            tag.includes('input') || \n            tag.includes('textarea') ||\n            node.onclick || node.oninput || node.onchange) {\n          elements.push(node);\n          // 高亮显示\n          node.style.border = '2px solid red';\n          node.style.backgroundColor = 'rgba(255,0,0,0.1)';\n        }\n        return NodeFilter.FILTER_SKIP;\n      }\n    }\n  );\n  \n  let node;\n  while (node = walker.nextNode()) {\n    // 已在acceptNode中处理\n  }\n  \n  return elements;\n}\n\nconst editableElements = scanEditableElements();\nconsole.log('可编辑元素数量:', editableElements.length);\neditableElements.forEach((el, i) => {\n  console.log(`元素 ${i}:`, el.tagName, el.className, el.id);\n});\n\n运行完毕后点击'下一步'",
