    if (data?.cancelled) {
      showMessage('元素拾取已取消', 'warn');
      setDomPickerState({ status: 'idle', message: '' });
      return;
    }
    if (data?.timeout) {
      showMessage('元素拾取超时', 'warn');
      setDomPickerState({ status: 'idle', message: '' });
      return;
    }
    if (data?.dom_path) {
      const normalizedPath = normalizeDomPathString(data.dom_path);
