function flushPendingMessages() {
  log(`flushPendingMessages called: win=${!!win}, visible=${win?.isVisible()}, pendingEvents=${pendingEvents.length}, pendingStatus=${!!pendingStatus}`);
  
  if (!win) {
    log('ERROR: No window to send messages to');
    return;
