function renderGraph() {
  if (!canvas) return;

  // Clear previous content
  while (canvas.firstChild) {
    canvas.removeChild(canvas.firstChild);
  }

  // Render container tree
  if (containerData) {
    const g = document.createElementNS('http://www.w3.org/2000/svg', 'g');
