    renderDomNodes(g, domData.slice(0, 10), 0, 0);

    canvas.appendChild(g);
  }

  // Draw connection lines
  if (containerData && domData && Array.isArray(domData)) {
    const connections = document.createElementNS('http://www.w3.org/2000/svg', 'g');
