  log(`Creating window (HEADLESS=${HEADLESS}, DEVTOOLS=${DEVTOOLS})`);
  log(`PROJECT_ROOT: ${PROJECT_ROOT}`);
  log(`DIST_DIR: ${DIST_DIR}`);
  
