  log(`Connecting to bus: ${BUS_URL}`);
  ws = new WebSocket(BUS_URL);
  
  ws.on('open', () => {
    log('Bus WebSocket OPEN event');
    busConnected = true;
    pendingStatus = { connected: true };
    
    // 立即发送总线状态
