// UI 诊断函数 - 检查服务连接、容器匹配和 DOM 获取状态

// 启动阶段快照系统
interface StageSnapshot {
  timestamp: number;
  stage: string;
  status: 'pending' | 'in_progress' | 'completed' | 'failed';
  data?: any;
  error?: string;
}

class StageTracker {
  private snapshots: Map<string, StageSnapshot> = new Map();
  
  setStage(stage: string, status: StageSnapshot['status'], data?: any, error?: string) {
    const snapshot: StageSnapshot = {
      timestamp: Date.now(),
      stage,
      status,
      data,
      error
    };
    this.snapshots.set(stage, snapshot);
    console.log(`[StageTracker] ${stage}: ${status}`, data ? `data=${JSON.stringify(data).slice(0,100)}` : '', error ? `error=${error}` : '');
  }
  
  getStage(stage: string): StageSnapshot | undefined {
    return this.snapshots.get(stage);
  }
  
  getAllSnapshots(): StageSnapshot[] {
    return Array.from(this.snapshots.values()).sort((a, b) => a.timestamp - b.timestamp);
  }
  
  clear() {
    this.snapshots.clear();
  }
}

export const stageTracker = new StageTracker();

// 初始化阶段快照
stageTracker.setStage('ui.init', 'in_progress');
stageTracker.setStage('api.check', 'pending');
stageTracker.setStage('bus.connect', 'pending');
stageTracker.setStage('services.health', 'pending');
stageTracker.setStage('containers.match', 'pending');
stageTracker.setStage('dom.fetch', 'pending');

export function runDiagnostics() {
  console.log('[diagnostics] Starting...');
  
  const results = {
    timestamp: new Date().toISOString(),
    services: checkServices(),
    api: checkAPI(),
    bus: checkBus(),
    data: checkData(),
    dom: checkDOMElements(),
    stages: stageTracker.getAllSnapshots(),
    timeline: buildTimeline()
  };
  
  console.log('[diagnostics] Results:', results);
  return results;
}

function checkServices() {
  const healthEl = document.getElementById('health') as HTMLDivElement;
  const statusEl = document.getElementById('status') as HTMLSpanElement;
  
  return {
    healthElementExists: !!healthEl,
    statusElementExists: !!statusEl,
    healthText: healthEl?.textContent || 'N/A',
    statusText: statusEl?.textContent || 'N/A',
    statusClass: statusEl?.className || 'N/A',
    isOk: statusEl?.className === 'ok' || false
  };
}

function checkAPI() {
  const apiAvailable = typeof window.api !== 'undefined';
  stageTracker.setStage('api.check', apiAvailable ? 'completed' : 'failed', undefined, apiAvailable ? undefined : 'window.api is undefined');
  
  return {
    apiAvailable,
    hasHealth: typeof window.api?.health === 'function',
    hasInvokeAction: typeof window.api?.invokeAction === 'function',
    hasOnBusEvent: typeof window.api?.onBusEvent === 'function',
    hasOnBusStatus: typeof window.api?.onBusStatus === 'function'
  };
}

function checkBus() {
  // 从事件日志推断总线状态
  const eventsEl = document.getElementById('events') as HTMLDivElement;
  const eventLogs = eventsEl?.textContent || '';
  
  const hasBusEvents = eventLogs.includes('bus event >>>');
  const hasBusStatus = eventLogs.includes('bus status >>>');
  const connectedEvent = eventLogs.match(/bus status.*connected.*true/i);
  const disconnectedEvent = eventLogs.match(/bus status.*connected.*false/i);
  
  const lastConnectedStatus = connectedEvent ? 'connected' : disconnectedEvent ? 'disconnected' : 'unknown';
  
  const busStatus = hasBusStatus && lastConnectedStatus === 'connected' ? 'completed' : 
                    (hasBusStatus && lastConnectedStatus === 'disconnected' ? 'failed' : 'pending');
  stageTracker.setStage('bus.connect', busStatus, { hasBusEvents, hasBusStatus }, 
                        lastConnectedStatus === 'disconnected' ? 'Bus disconnected' : undefined);
  
  return {
    hasBusEvents,
    hasBusStatus,
    lastConnectedStatus,
    status: busStatus
  };
}

function checkData() {
  const containerTreeEl = document.getElementById('containerTree') as HTMLDivElement;
  const domTreeEl = document.getElementById('domTree') as HTMLDivElement;
  
  let containerData = null;
  let domData = null;
  
  try {
    if (containerTreeEl?.textContent) {
      containerData = JSON.parse(containerTreeEl.textContent);
    }
  } catch {
    containerData = { error: 'parse_failed' };
  }
  
  try {
    if (domTreeEl?.textContent) {
      domData = JSON.parse(domTreeEl.textContent);
    }
  } catch {
    domData = { error: 'parse_failed' };
  }
  
  const containerFilled = !!containerData && !containerData.error;
  const domFilled = !!domData && !domData.error;
  
  stageTracker.setStage('containers.match', containerFilled ? 'completed' : 'pending', 
                        { containerDataKeys: containerData ? Object.keys(containerData) : [] },
                        !containerFilled ? 'Container data not available' : undefined);
  
  stageTracker.setStage('dom.fetch', domFilled ? 'completed' : 'pending',
                        { domDataKeys: domData ? Object.keys(domData) : [] },
                        !domFilled ? 'DOM data not available' : undefined);
  
  return {
    lastSnapshotExists: typeof window.lastSnapshot !== 'undefined',
    lastSnapshotKeys: window.lastSnapshot ? Object.keys(window.lastSnapshot) : [],
    containerTreeFilled: containerFilled,
    containerTreeHasData: containerData && Object.keys(containerData).length > 0,
    domTreeFilled: domFilled,
    domTreeHasData: domData && Object.keys(domData).length > 0
  };
}

function checkDOMElements() {
  return {
    containerTreeElementExists: !!document.getElementById('containerTree'),
    domTreeElementExists: !!document.getElementById('domTree'),
    graphPanelElementExists: !!document.getElementById('graphPanel'),
    eventsElementExists: !!document.getElementById('events'),
    statusElementExists: !!document.getElementById('status'),
    healthElementExists: !!document.getElementById('health'),
    modeElementExists: !!document.getElementById('mode'),
    btnHealthExists: !!document.getElementById('btnHealth'),
    btnClearExists: !!document.getElementById('btnClear'),
    btnDiagExists: !!document.getElementById('btnDiag')
  };
}

function buildTimeline(): string[] {
  const snapshots = stageTracker.getAllSnapshots();
  const timeline: string[] = [];
  
  if (snapshots.length === 0) {
    timeline.push('无启动阶段记录');
    return timeline;
  }
  
  const startTime = snapshots[0].timestamp;
  
  for (const snap of snapshots) {
    const elapsed = snap.timestamp - startTime;
    const timeStr = `${elapsed}ms`;
    const statusIcon = snap.status === 'completed' ? '✓' : 
                      snap.status === 'failed' ? '✗' : 
                      snap.status === 'in_progress' ? '⏳' : '○';
    const errorStr = snap.error ? ` (错误: ${snap.error})` : '';
    timeline.push(`[${timeStr}] ${statusIcon} ${snap.stage}${errorStr}`);
  }
  
  return timeline;
}

export function formatDiagnosticReport(results: ReturnType<typeof runDiagnostics>): string {
  const lines = [
    '=== UI 诊断报告 ===',
    `时间: ${results.timestamp}`,
    '',
    '【1. 服务连接状态】',
    `  健康元素: ${results.services.healthElementExists ? '✓' : '✗'}`,
    `  状态元素: ${results.services.statusElementExists ? '✓' : '✗'}`,
    `  健康文本: ${results.services.healthText}`,
    `  状态文本: ${results.services.statusText}`,
    `  状态类名: ${results.services.statusClass}`,
    `  连接正常: ${results.services.isOk ? '✓' : '✗'}`,
    '',
    '【2. API 检查】',
    `  API 可用: ${results.api.apiAvailable ? '✓' : '✗'}`,
    `  health(): ${results.api.hasHealth ? '✓' : '✗'}`,
    `  invokeAction(): ${results.api.hasInvokeAction ? '✓' : '✗'}`,
    `  onBusEvent(): ${results.api.hasOnBusEvent ? '✓' : '✗'}`,
    `  onBusStatus(): ${results.api.hasOnBusStatus ? '✓' : '✗'}`,
    '',
    '【3. 总线连接】',
    `  总线事件: ${results.bus.hasBusEvents ? '✓' : '✗'}`,
    `  总线状态: ${results.bus.hasBusStatus ? '✓' : '✗'}`,
    `  连接状态: ${results.bus.lastConnectedStatus}`,
    `  总线阶段: ${results.bus.status}`,
    '',
    '【4. 数据状态】',
    `  快照存在: ${results.data.lastSnapshotExists ? '✓' : '✗'}`,
    `  快照字段: ${results.data.lastSnapshotKeys.join(', ') || '无'}`,
    `  容器树填充: ${results.data.containerTreeFilled ? '✓' : '✗'}`,
    `  容器树数据: ${results.data.containerTreeHasData ? '✓' : '✗'}`,
    `  DOM 树填充: ${results.data.domTreeFilled ? '✓' : '✗'}`,
    `  DOM 树数据: ${results.data.domTreeHasData ? '✓' : '✗'}`,
    '',
    '【5. DOM 元素检查】',
    `  containerTree: ${results.dom.containerTreeElementExists ? '✓' : '✗'}`,
    `  domTree: ${results.dom.domTreeElementExists ? '✓' : '✗'}`,
    `  graphPanel: ${results.dom.graphPanelElementExists ? '✓' : '✗'}`,
    `  events: ${results.dom.eventsElementExists ? '✓' : '✗'}`,
    `  status: ${results.dom.statusElementExists ? '✓' : '✗'}`,
    `  health: ${results.dom.healthElementExists ? '✓' : '✗'}`,
    `  mode: ${results.dom.modeElementExists ? '✓' : '✗'}`,
    `  btnHealth: ${results.dom.btnHealthExists ? '✓' : '✗'}`,
    `  btnClear: ${results.dom.btnClearExists ? '✓' : '✗'}`,
    `  btnDiag: ${results.dom.btnDiagExists ? '✓' : '✗'}`,
    '',
    '【6. 启动时间线】',
    ...results.timeline.map(line => `  ${line}`),
    '',
    '=== 问题分析 ===',
    ...analyzeResults(results)
  ];
  
  return lines.join('\n');
}

function analyzeResults(results: ReturnType<typeof runDiagnostics>): string[] {
  const issues: string[] = [];
  
  // 服务连接问题
  if (!results.api.apiAvailable) {
    issues.push('❌ [关键] API 未加载 - preload 脚本可能未正确加载');
  }
  if (!results.services.isOk) {
    issues.push('⚠️  服务未连接 - 检查底层服务是否运行 (端口 7701, 7704)');
  }
  
  // 总线问题
  if (!results.bus.hasBusStatus) {
    issues.push('❌ [关键] 总线状态未收到 - WebSocket 连接可能失败');
  }
  if (results.bus.lastConnectedStatus === 'disconnected') {
    issues.push('❌ [关键] 总线已断开 - WebSocket 连接中断');
  }
  if (results.bus.status === 'pending') {
    issues.push('⚠️  总线连接尚未建立 - 检查 Bus URL: ws://127.0.0.1:7701/bus');
  }
  
  // 数据问题
  if (!results.data.lastSnapshotExists) {
    issues.push('❌ 快照未收到 - 容器匹配可能失败');
  }
  if (results.data.lastSnapshotExists && !results.data.lastSnapshotKeys.includes('container_tree')) {
    issues.push('⚠️  快照缺少 container_tree - 数据结构可能不匹配');
  }
  if (results.data.lastSnapshotExists && !results.data.lastSnapshotKeys.includes('dom_tree')) {
    issues.push('⚠️  快照缺少 dom_tree - DOM 数据可能未获取');
  }
  if (!results.data.containerTreeFilled) {
    issues.push('❌ 容器树未更新 - UI 未显示容器数据');
  }
  if (!results.data.domTreeFilled) {
    issues.push('❌ DOM 树未更新 - UI 未显示 DOM 数据');
  }
  
  // DOM 元素问题
  if (!results.dom.containerTreeElementExists) {
    issues.push('❌ containerTree 元素缺失 - HTML 结构问题');
  }
  if (!results.dom.domTreeElementExists) {
    issues.push('❌ domTree 元素缺失 - HTML 结构问题');
  }
  if (!results.dom.btnDiagExists) {
    issues.push('⚠️  诊断按钮缺失 - HTML 可能未更新');
  }
  
  // 启动时间线分析
  const failedStages = results.stages.filter(s => s.status === 'failed');
  if (failedStages.length > 0) {
    issues.push(`\n❌ 失败的阶段: ${failedStages.map(s => s.stage).join(', ')}`);
  }
  
  if (issues.length === 0) {
    return ['✅ 所有检查通过，UI 正常运行'];
  }
  
  return issues;
}

// 导出用于调试的函数
export function getSnapshots(): StageSnapshot[] {
  return stageTracker.getAllSnapshots();
}

export function updateStage(stage: string, status: StageSnapshot['status'], data?: any, error?: string) {
  stageTracker.setStage(stage, status, data, error);
}

