// Find DOM node position by CSS selector
function findDomNodePositionBySelector(domTree, selector) {
  if (!domTree || !selector) return null;

  // Simple CSS selector matching
  if (selector.startsWith('#')) {
    const targetId = selector.substring(1);
    return findNodeById(domTree, targetId);
  } else if (selector.startsWith('.')) {
    const targetClass = selector.substring(1);
