function findNodeById(node, targetId) {
  if (node.id === targetId) return node.path;
  if (node.children) {
    for (const child of node.children) {
      const result = findNodeById(child, targetId);
      if (result) return result;
    }
  }
  return null;
}

function findNodeByClass(node, targetClass) {
  if (node.classes && node.classes.includes(targetClass)) return node.path;
  if (node.children) {
    for (const child of node.children) {
      const result = findNodeByClass(child, targetClass);
      if (result) return result;
    }
  }
  return null;
}

function findNodeByTag(node, targetTag) {
  if (node.tag && node.tag.toLowerCase() === targetTag.toLowerCase()) return node.path;
  if (node.children) {
    for (const child of node.children) {
      const result = findNodeByTag(child, targetTag);
      if (result) return result;
    }
  }
  return null;
}

