#!/usr/bin/env node
/**
 * å¯åŠ¨è„šæœ¬ - æ”¯æŒå‘½ä»¤è¡Œå‚æ•°
 * ç”¨æ³•ï¼šnode scripts/start-headful.mjs [--profile <name>] [--url <url>]
 *       node scripts/start-headful.mjs                  # ä½¿ç”¨é»˜è®¤é…ç½®ï¼ˆweibo_fresh, weibo.comï¼‰
 *       node scripts/start-headful.mjs --profile weibo_fresh https://weibo.com
 *       node scripts/start-headful.mjs --url https://weibo.com
 */

import { spawn } from 'node:child_process';
import path from 'node:path';

const __filename = import.meta.url;
const __dirname = path.dirname(__filename);
const launcherPath = path.resolve(__dirname, 'launcher/core/launcher.mjs');

/**
 * è§£æå‘½ä»¤è¡Œå‚æ•°
 * æ”¯æŒï¼š--profile <name> å’Œ --url <url>
 */
function parseArgs() {
  const args = process.argv.slice(2);
  const result = {
    profile: null as string | null,
    url: null as string | null
  };
  
  for (let i = 0; i < args.length; i++) {
    const arg = args[i];
    
    if (arg === '--profile' || arg === '-p') {
      if (i + 1 < args.length) {
        result.profile = args[i + 1];
        i++; // è·³è¿‡å‚æ•°å€¼
      }
    } else if (arg === '--url' || arg === '-u') {
      if (i + 1 < args.length) {
        result.url = args[i + 1];
        i++; // è·³è¿‡å‚æ•°å€¼
      }
    } else if (arg === '--headless' || arg === '-h') {
      // headless å‚æ•°ä¼ é€’ç»™ launcher
      // ä¸åœ¨ parseArgs ä¸­å¤„ç†ï¼Œç›´æ¥ä¼ é€’æ‰€æœ‰å‰©ä½™å‚æ•°
      break;
    }
  }
  
  return result;
}

function main() {
  const { profile, url, headless } = parseArgs();
  
  const defaultProfile = 'weibo_fresh';
  const defaultUrl = 'https://weibo.com';
  
  const profileName = profile || defaultProfile;
  const targetUrl = url || defaultUrl;
  
  // å°†æ‰€æœ‰å‰©ä½™å‚æ•°ä¼ é€’ç»™ launcherï¼ˆåŒ…æ‹¬ --headlessï¼‰
  const extraArgs = process.argv.slice(2).filter(arg => arg !== '--profile' && arg !== '-p' && arg !== '--url' && arg !== '-u' && arg !== '-h');
  
  const args = [launcherPath, '--profile', profileName, '--url', targetUrl, ...extraArgs];
  
  console.log('ğŸš€ WebAuto ä¸€é”®å¯åŠ¨');
  console.log(`  Profile: ${profileName}`);
  console.log(`  URL: ${targetUrl}`);
  console.log(`  å‚æ•°: ${extraArgs.join(' ')}`);
  console.log();
  
  const child = spawn('node', args, {
    stdio: 'inherit',
    env: process.env
  });

  child.on('exit', (code) => {
    process.exit(code);
  });
}

main();

