import WebSocket from 'ws';

async function testHighlight() {
  console.log('1. 测试 unified-api /health');
  const healthRes = await fetch('http://127.0.0.1:7701/health');
  console.log('   健康检查:', await healthRes.json());

  console.log('\n2. 测试浏览器服务健康');
  const browserHealthRes = await fetch('http://127.0.0.1:7704/health');
  console.log('   浏览器健康:', await browserHealthRes.json());

  console.log('\n3. 测试 containers:match 动作');
  const ws = new WebSocket('ws://127.0.0.1:7701/ws');
  await new Promise((resolve) => ws.on('open', resolve));

  const matchResult = await new Promise((resolve) => {
    ws.on('message', (data) => {
      const payload = JSON.parse(data.toString());
      if (payload.type === 'response') {
        resolve(payload);
      } else if (payload.type === 'error') {
        resolve(payload);
      }
    });
    ws.send(JSON.stringify({
      type: 'action',
      action: 'containers:match',
      payload: { profile: 'weibo_fresh', url: 'https://weibo.com', maxDepth: 6, maxChildren: 20 }
    }));
  });
  ws.close();
  console.log('   容器匹配结果:', matchResult);

  console.log('\n4. 测试 browser:highlight 动作（高亮 weibo 头部）');
  const ws2 = new WebSocket('ws://127.0.0.1:7701/ws');
  await new Promise((resolve) => ws2.on('open', resolve));

  const highlightResult = await new Promise((resolve) => {
    ws2.on('message', (data) => {
      const payload = JSON.parse(data.toString());
      if (payload.type === 'response') {
        resolve(payload);
      } else if (payload.type === 'error') {
        resolve(payload);
      }
    });
    ws2.send(JSON.stringify({
      type: 'action',
      action: 'browser:highlight',
      payload: { selector: '.WB_global_nav.WB_global_nav_v2', color: 'red' }
    }));
  });
  ws2.close();
  console.log('   高亮结果:', highlightResult);

  console.log('\n✅ 基础能力测试完成');
}

testHighlight().catch(err => {
  console.error('❌ 测试失败:', err);
  process.exit(1);
});
