import WebSocket from 'ws';

const WS_URL = 'ws://127.0.0.1:7701/ws';

async function testHighlight() {
  console.log('[TEST] 连接 WS', WS_URL);
  const ws = new WebSocket(WS_URL);

  await new Promise((resolve, reject) => {
    ws.on('open', resolve);
    ws.on('error', reject);
    setTimeout(() => reject(new Error('WS open timeout')), 5000);
  });
  console.log('[TEST] WS 已连接');

  // 测试 selector 高亮
  const payload1 = {
    type: 'action',
    action: 'browser:highlight',
    payload: { selector: 'div[class*="LoginCard_"]', color: '#ff0000', style: '2px solid' }
  };
  console.log('[TEST] 发送 browser:highlight (selector)');
  const res1 = await sendMessage(ws, payload1);
  console.log('[TEST] browser:highlight 结果 success=' + (res1?.success) + ' error=' + (res1?.error || 'none'));

  // 测试 dom_path 高亮
  const payload2 = {
    type: 'action',
    action: 'browser:highlight-dom-path',
    payload: { dom_path: 'root/0/0', root_selector: 'div[class*="LoginCard_"]', color: '#00ff00', style: '3px dashed' }
  };
  console.log('[TEST] 发送 browser:highlight-dom-path');
  const res2 = await sendMessage(ws, payload2);
  console.log('[TEST] browser:highlight-dom-path 结果 success=' + (res2?.success) + ' error=' + (res2?.error || 'none'));

  ws.close();
  return { selector: res1, domPath: res2 };
}

function sendMessage(ws, payload) {
  return new Promise((resolve, reject) => {
    const timer = setTimeout(() => reject(new Error('操作超时')), 10000);
    const action = payload.action;

    ws.on('message', (data) => {
      try {
        const msg = JSON.parse(data.toString());
        if (msg.type === 'response' && msg.action === action) {
          clearTimeout(timer);
          resolve(msg);
        }
        if (msg.type === 'error') {
          clearTimeout(timer);
          reject(new Error(msg.error || 'WS error'));
        }
      } catch (e) {
        clearTimeout(timer);
        reject(e);
      }
    });

    ws.send(JSON.stringify(payload));
  });
}

testHighlight()
  .then((res) => {
    console.log('[TEST] ✅ 高亮测试完成');
    process.exit(0);
  })
  .catch((err) => {
    console.error('[TEST] ❌ 失败:', err.message);
    process.exit(1);
  });
