  console.log('🔧 [build:services] 开始编译所有服务产物...');
  // 1. 采集并输出构建依赖
  try {
    await run('npm run prebuild --silent || true', 'pipe');
  } catch {}

  // 2. 编译 TypeScript（不阻断后续构建，仅记录问题）
  await run('tsc --project tsconfig.services.json --noEmit || true', 'inherit');

  // 3. 复制 libs/browser 到 dist/libs/browser（兼容 JS 入口）
  try {
    execSync('cp -R libs/browser dist/libs/browser || true', { stdio:'ignore' });
  } catch {}

  // 4. 构建 unified-api（输出到 dist/services/unified-api），使用CJS格式保持兼容性
  try { fs.mkdirSync(path.resolve(repoRoot, 'dist/services/unified-api'), { recursive: true }); } catch {}
  await run('npx esbuild services/unified-api/index.js --bundle --platform=node --format=cjs --outfile=dist/services/unified-api/index.js', 'inherit');

  // 5. 确认 workflow api (sharedmodule) 已存在
  const workflowPath = 'dist/sharedmodule/engines/api-gateway/server.js';
  if (!fs.existsSync(path.resolve(repoRoot, workflowPath))) {
    throw new Error(`Workflow API 产物缺失: ${workflowPath}`);
  }

  // 6. 验证构建产物存在
  const verify = [
    'dist/sharedmodule/engines/api-gateway/server.js',
    'dist/services/unified-api/index.js',
    'dist/services/browser-service/index.js',
  ];
  for (const f of verify){
    if (!fs.existsSync(path.resolve(repoRoot, f))) {
      throw new Error(`产物缺失: ${f}`);
    }
  }

  // 7. 健康检查＝真实启动握手与 bus 链路验证
  if (process.env.WEBAUTO_SKIP_HEALTH_CHECK !== '1') {
    await healthCheck();
  } else {
    console.log('[build:services] skip health check (WEBAUTO_SKIP_HEALTH_CHECK=1)');
  }

  // 8. 留出空隙给后续流程（避免健康检查结束后立即退出）
  await wait(200);

  return;
}

main().catch(err => {
  console.error(err);
  process.exit(1);
});

