/**
 * containers:match action handler
 * 支持 profile/url 匹配并返回 container_tree + dom_tree
 */
import { UiController } from './controller.js';

export async function handleContainersMatch(controller, payload) {
  const { profile, url, maxDepth, maxChildren } = payload;

  if (!profile) {
    return { success: false, error: '缺少 profile' };
  }

  try {
    // 调用已有的 inspect_tree 逻辑
    const result = await controller.handleContainerInspect({
      profile,
      url,
      maxDepth,
      maxChildren
    });

    if (!result.success) {
      return { success: false, error: result.error || 'container inspect 失败' };
    }

    const snapshot = result.data?.snapshot;
    if (!snapshot) {
      return { success: false, error: '缺少 snapshot' };
    }

    // 构造 containers:match 期望的返回格式
    return {
      success: true,
      data: {
        container_tree: snapshot.container_tree || null,
        dom_tree: snapshot.dom_tree || null,
        metadata: {
          root_container_id: snapshot.metadata?.root_container_id || snapshot.container_tree?.id
        }
      }
    };
  } catch (err) {
    return { success: false, error: err.message };
  }
}
