  }

  async verifyGetStatus() {
    const result = await this.service.getStatus();
    return {
      ok: result.success,
      sessions: result.sessions,
      raw: result.raw
    };
  }

  async verifyCommand(action, args = {}) {
    const result = await this.service.command(action, args);
    return {
      ok: !!result.ok,
