import { readFileSync, writeFileSync } from 'fs';

const cliPath = 'dist/modules/browser-control/src/cli.js';
let content = readFileSync(cliPath, 'utf-8');

// 在 yargs command 区段插入两个新子命令
const highlightCmd = `
  .command('highlight', '高亮指定 selector', (yargs) => {
    return yargs
      .option('url', { describe: '目标页面 URL', type: 'string', demandOption: true })
      .option('selector', { describe: 'CSS 选择器', type: 'string', demandOption: true })
      .option('color', { describe: '高亮颜色', type: 'string', default: 'green' })
      .option('duration', { describe: '持续时间(ms)', type: 'number', default: 1500 })
      .option('profile', { describe: 'profile 名', type: 'string', default: 'weibo_fresh' });
  }, async (argv) => {
    const result = await runHighlight(argv);
    console.log(JSON.stringify(result));
  })
  .command('highlight-dom-path', '高亮指定 dom_path', (yargs) => {
    return yargs
      .option('url', { describe: '目标页面 URL', type: 'string', demandOption: true })
      .option('dom-path', { describe: 'DOM 路径，如 root/0/1', type: 'string', demandOption: true })
      .option('color', { describe: '高亮颜色', type: 'string', default: 'green' })
      .option('duration', { describe: '持续时间(ms)', type: 'number', default: 1500 })
      .option('profile', { describe: 'profile 名', type: 'string', default: 'weibo_fresh' });
  }, async (argv) => {
    const result = await runHighlightDomPath(argv);
    console.log(JSON.stringify(result));
  })
`;

// 插入位置：在 .command('dom-dump', ...) 后面
content = content.replace(
  /(\s+\.command\('dom-dump'[\s\S]+?\}))/,
  `$1${highlightCmd}`
);

// 新增两个运行函数
const runHighlight = `
async function runHighlight(argv) {
  const session = await openSession(argv);
  const style = \`4px solid \${argv.color}\`;
  const duration = argv.duration;
  const result = await session.page.evaluate((data) => {
    const nodes = Array.from(document.querySelectorAll(data.selector));
    if (!nodes.length) return { success: false, error: 'element not found', count: 0 };
    const cleanups = nodes.map(el => {
      const orig = el.style.outline;
      el.style.outline = data.style;
      return () => { el.style.outline = orig; };
    });
    const cleanup = () => cleanups.forEach(fn => { try { fn(); } catch {} });
    if (data.duration > 0) setTimeout(cleanup, data.duration);
    return { success: true, count: nodes.length };
  }, { selector: argv.selector, style, duration });
  await session.close();
  return result;
}
`;

const runHighlightDomPath = `
async function runHighlightDomPath(argv) {
  const session = await openSession(argv);
  const path = argv['dom-path'];
  const style = \`4px solid \${argv.color}\`;
  const duration = argv.duration;
  const result = await session.page.evaluate((data) => {
    const parts = data.path.split('/').filter(t => t.length);
    if (parts[0] !== 'root') parts.unshift('root');
    let node = document.body || document.documentElement;
    for (let i = 1; i < parts.length; i++) {
      const idx = Number(parts[i]);
      const children = node.children ? Array.from(node.children) : [];
      if (!Number.isFinite(idx) || idx < 0 || idx >= children.length) {
        return { success: false, error: 'dom path not found', count: 0 };
      }
      node = children[idx];
      if (!node) return { success: false, error: 'dom path not found', count: 0 };
    }
    const orig = node.style.outline;
    node.style.outline = data.style;
    if (data.duration > 0) setTimeout(() => { node.style.outline = orig; }, data.duration);
    return { success: true, count: 1 };
  }, { path, style, duration });
  await session.close();
  return result;
}
`;

// 在文件尾部追加函数定义
content += '\n' + runHighlight + '\n' + runHighlightDomPath;

writeFileSync(cliPath, content, 'utf-8');
console.log('已给 browser-control CLI 新增 highlight / highlight-dom-path 命令');
