    return ipcRenderer.invoke('ui:action', { action, payload });
  },
  minimize: () => {
    console.log('[preload] minimize() called');
    return ipcRenderer.invoke('window:minimize');
  },
  close: () => {
    console.log('[preload] close() called');
    return ipcRenderer.invoke('window:close');
