import { ConfigManager } from './ConfigManager.js';

// 相机状态接口
export interface CameraStatus {
  ip: string;
  name: string; // 相机别名
  model: string;
  firmware: string;
  mac: string;
  serialNumber: string;
  isConnected: boolean;
  batteryLevel?: number;
  storageCapacity?: number;
  storageAvailable?: number;
  recording?: boolean;
  batteryVoltage?: number;
  temperature?: number;
  panPosition?: number;
  tiltPosition?: number;
  focusDistance?: number;
}

// 相机上下文接口
export interface CameraContext {
  currentCamera: string | null; // 当前相机IP
  cameras: Map<string, CameraStatus>; // IP到相机状态的映射
}

// 相机信息接口（用于WebSocket更新）
export interface CameraInfo {
  ip: string;
  name: string;
  model: string;
  firmware: string;
  mac: string;
  serialNumber: string;
  isConnected: boolean;
  recording?: boolean;
  batteryVoltage?: number;
  temperature?: number;
  panPosition?: number;
  tiltPosition?: number;
  focusDistance?: number;
}

