   264â†’        </CardContent>
   265â†’      </Card>
   266â†’
   267â†’      {/* Conditions */}
   268â†’      <Card>
   269â†’        <CardHeader>
   270â†’          <div className="flex items-center justify-between">
   271â†’            <div>
