   289â†’            <div className="space-y-3">
   290â†’              {currentRule.conditions.map((condition, index) => (
   291â†’                <div key={index} className="border rounded-lg p-4 bg-gray-50">
   292â†’                  <div className="flex items-center justify-between mb-3">
