   294â†’                    <div className="flex items-center space-x-2">
   295â†’                      <Button
   296â†’                        variant="outline"
   297â†’                        size="sm"
   298â†’                        onClick={() => moveCondition(index, 'up')}
   299â†’                        disabled={index === 0}
   300â†’                      >
   301â†’                        <ArrowUp className="w-4 h-4" />
   302â†’                      </Button>
   303â†’                      <Button
   304â†’                        variant="outline"
   305â†’                        size="sm"
