   307â†’                        disabled={index === currentRule.conditions.length - 1}
   308â†’                      >
   309â†’                        <ArrowDown className="w-4 h-4" />
   310â†’                      </Button>
   311â†’                      <Button
   312â†’                        variant="outline"
   313â†’                        size="sm"
   314â†’                        onClick={() => duplicateCondition(condition)}
   315â†’                      >
   316â†’                        <Copy className="w-4 h-4" />
   317â†’                      </Button>
   318â†’                      <Button
   319â†’                        variant="outline"
   320â†’                        size="sm"
   321â†’                        onClick={() => removeCondition(index)}
   322â†’                      >
   323â†’                        <Trash2 className="w-4 h-4" />
   324â†’                      </Button>
   325â†’                    </div>
   326â†’                  </div>
   327â†’
   328â†’                  <div className="grid grid-cols-1 md:grid-cols-4 gap-3">
   329â†’                    <Select
   330â†’                      value={condition.type}
