   359â†’                      </SelectTrigger>
   360â†’                      <SelectContent>
   361â†’                        <SelectItem value="equals">Equals</SelectItem>
   362â†’                        <SelectItem value="not_equals">Not Equals</SelectItem>
   363â†’                        <SelectItem value="contains">Contains</SelectItem>
   364â†’                        <SelectItem value="not_contains">Not Contains</SelectItem>
