   387â†’                      onChange={(e) => updateCondition(index, {
   388â†’                        ...condition,
   389â†’                        weight: parseInt(e.target.value) || 1
   390â†’                      })}
   391â†’                      placeholder="Weight"
   392â†’                    />
   393â†’                  </div>
   394â†’                </div>
   395â†’              ))}
   396â†’            </div>
   397â†’          )}
   398â†’        </CardContent>
   399â†’      </Card>
   400â†’
   401â†’      {/* Actions */}
   402â†’      <Card>
   403â†’        <CardHeader>
   404â†’          <div className="flex items-center justify-between">
   405â†’            <div>
   406â†’              <CardTitle>Actions</CardTitle>
   407â†’              <CardDescription>
   408â†’                Define what happens when this rule is triggered
   409â†’              </CardDescription>
   410â†’            </div>
   411â†’            <Button onClick={addAction} size="sm">
   412â†’              <Plus className="w-4 h-4 mr-2" />
   413â†’              Add Action
   414â†’            </Button>
   415â†’          </div>
   416â†’        </CardHeader>
   417â†’        <CardContent>
   418â†’          {currentRule.actions.length === 0 ? (
   419â†’            <div className="text-center py-8 text-gray-500 border-2 border-dashed border-gray-300 rounded-lg">
   420â†’              No actions configured. Add an action to get started.
   421â†’            </div>
   422â†’          ) : (
   423â†’            <div className="space-y-3">
   424â†’              {currentRule.actions.map((action, index) => (
   425â†’                <div key={index} className="border rounded-lg p-4 bg-gray-50">
   426â†’                  <div className="flex items-center justify-between mb-3">
   427â†’                    <span className="text-sm font-medium">Action {index + 1}</span>
   428â†’                    <div className="flex items-center space-x-2">
   429â†’                      <Button
   430â†’                        variant="outline"
   431â†’                        size="sm"
   432â†’                        onClick={() => moveAction(index, 'up')}
   433â†’                        disabled={index === 0}
   434â†’                      >
   435â†’                        <ArrowUp className="w-4 h-4" />
   436â†’                      </Button>
   437â†’                      <Button
   438â†’                        variant="outline"
   439â†’                        size="sm"
   440â†’                        onClick={() => moveAction(index, 'down')}
   441â†’                        disabled={index === currentRule.actions.length - 1}
   442â†’                      >
   443â†’                        <ArrowDown className="w-4 h-4" />
   444â†’                      </Button>
   445â†’                      <Button
   446â†’                        variant="outline"
   447â†’                        size="sm"
   448â†’                        onClick={() => duplicateAction(action)}
   449â†’                      >
   450â†’                        <Copy className="w-4 h-4" />
   451â†’                      </Button>
   452â†’                      <Button
   453â†’                        variant="outline"
   454â†’                        size="sm"
   455â†’                        onClick={() => removeAction(index)}
   456â†’                      >
   457â†’                        <Trash2 className="w-4 h-4" />
   458â†’                      </Button>
   459â†’                    </div>
   460â†’                  </div>
   461â†’
   462â†’                  <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
   463â†’                    <Select
   464â†’                      value={action.type}
   465â†’                      onValueChange={(value) => updateAction(index, {
   466â†’                        ...action,
   467â†’                        type: value as RoutingAction['type']
   468â†’                      })}
   469â†’                    >
   470â†’                      <SelectTrigger>
   471â†’                        <SelectValue />
   472â†’                      </SelectTrigger>
   473â†’                      <SelectContent>
   474â†’                        <SelectItem value="route_to">Route To</SelectItem>
   475â†’                        <SelectItem value="modify_request">Modify Request</SelectItem>
   476â†’                        <SelectItem value="add_header">Add Header</SelectItem>
   477â†’                        <SelectItem value="set_param">Set Parameter</SelectItem>
   478â†’                        <SelectItem value="transform">Transform</SelectItem>
   479â†’                        <SelectItem value="log">Log</SelectItem>
   480â†’                        <SelectItem value="metric">Metric</SelectItem>
   481â†’                      </SelectContent>
   482â†’                    </Select>
   483â†’
   484â†’                    <Input
   485â†’                      value={typeof action.value === 'string' ? action.value : JSON.stringify(action.value)}
   486â†’                      onChange={(e) => {
   487â†’                        let value: any = e.target.value;
   488â†’                        try {
   489â†’                          // Try to parse as JSON for complex values
   490â†’                          if (e.target.value.startsWith('{') || e.target.value.startsWith('[')) {
   491â†’                            value = JSON.parse(e.target.value);
   492â†’                          }
   493â†’                        } catch {
   494â†’                          // Keep as string if not valid JSON
   495â†’                        }
   496â†’                        updateAction(index, { ...action, value });
   497â†’                      }}
   498â†’                      placeholder="Value (JSON or string)"
   499â†’                    />
   500â†’
   501â†’                    <div className="text-sm text-gray-500">
   502â†’                      Type: {action.type}
   503â†’                    </div>
   504â†’                  </div>
   505â†’                </div>
   506â†’              ))}
   507â†’            </div>
   508â†’          )}
   509â†’        </CardContent>
   510â†’      </Card>
   511â†’
   512â†’      {/* Test Section */}
   513â†’      <Card>
   514â†’        <CardHeader>
   515â†’          <div className="flex items-center justify-between">
   516â†’            <div>
   517â†’              <CardTitle>Test Rule</CardTitle>
   518â†’              <CardDescription>
   519â†’                Test this rule with a sample request
   520â†’              </CardDescription>
   521â†’            </div>
   522â†’            <Button
   523â†’              variant="outline"
   524â†’              size="sm"
   525â†’              onClick={() => setShowTestRequest(!showTestRequest)}
   526â†’            >
   527â†’              {showTestRequest ? <EyeOff className="w-4 h-4 mr-2" /> : <Eye className="w-4 h-4 mr-2" />}
   528â†’              {showTestRequest ? 'Hide' : 'Show'} Test Request
   529â†’            </Button>
   530â†’          </div>
   531â†’        </CardHeader>
   532â†’
   533â†’        {showTestRequest && (
   534â†’          <CardContent className="space-y-4">
   535â†’            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
   536â†’              <div>
   537â†’                <label className="block text-sm font-medium mb-2">Model</label>
   538â†’                <Input
   539â†’                  value={testRequest.model}
   540â†’                  onChange={(e) => setTestRequest(prev => ({ ...prev, model: e.target.value }))}
   541â†’                  placeholder="gpt-4"
   542â†’                />
   543â†’              </div>
   544â†’              <div>
   545â†’                <label className="block text-sm font-medium mb-2">Max Tokens</label>
   546â†’                <Input
   547â†’                  type="number"
   548â†’                  value={testRequest.max_tokens || ''}
   549â†’                  onChange={(e) => setTestRequest(prev => ({
   550â†’                    ...prev,
   551â†’                    max_tokens: e.target.value ? parseInt(e.target.value) : undefined
   552â†’                  }))}
   553â†’                  placeholder="2048"
   554â†’                />
   555â†’              </div>
   556â†’            </div>
   557â†’
   558â†’            <div>
   559â†’              <label className="block text-sm font-medium mb-2">Message</label>
   560â†’              <textarea
   561â†’                className="w-full p-3 border rounded-lg"
   562â†’                rows={4}
   563â†’                value={testRequest.messages[0]?.content || ''}
   564â†’                onChange={(e) => setTestRequest(prev => ({
   565â†’                  ...prev,
   566â†’                  messages: [{ role: 'user', content: e.target.value }]
   567â†’                }))}
   568â†’                placeholder="Enter your test message here..."
   569â†’              />
   570â†’            </div>
   571â†’
   572â†’            <Button onClick={handleTest} disabled={testing} className="w-full">
   573â†’              <TestTube className="w-4 h-4 mr-2" />
   574â†’              {testing ? 'Testing...' : 'Test Rule'}
   575â†’            </Button>
   576â†’
   577â†’            {testResult && (
   578â†’              <div className="mt-4 p-4 border rounded-lg bg-blue-50">
   579â†’                <h4 className="font-medium mb-2">Test Results</h4>
   580â†’                <div className="space-y-2 text-sm">
   581â†’                  <div><strong>Matched:</strong> {testResult.matched ? 'Yes' : 'No'}</div>
   582â†’                  <div><strong>Confidence:</strong> {testResult.confidence}%</div>
   583â†’                  <div><strong>Selected Route:</strong> {testResult.selectedRoute}</div>
   584â†’                  <div><strong>Selected Provider:</strong> {testResult.selectedProvider}</div>
   585â†’                  <div><strong>Selected Model:</strong> {testResult.selectedModel}</div>
   586â†’                  <div><strong>Reasoning:</strong> {testResult.reasoning}</div>
   587â†’                  <div><strong>Execution Time:</strong> {testResult.executionTime}ms</div>
   588â†’                  {testResult.matchedRules.length > 0 && (
   589â†’                    <div><strong>Matched Rules:</strong> {testResult.matchedRules.map(r => r.name).join(', ')}</div>
   590â†’                  )}
   591â†’                </div>
   592â†’              </div>
   593â†’            )}
   594â†’          </CardContent>
   595â†’        )}
   596â†’      </Card>
   597â†’
   598â†’      {/* Actions */}
   599â†’      <div className="flex justify-end space-x-4">
   600â†’        <Button variant="outline" onClick={onCancel}>
   601â†’          Cancel
   602â†’        </Button>
   603â†’        <Button onClick={handleSave}>
   604â†’          <Save className="w-4 h-4 mr-2" />
   605â†’          {isNew ? 'Create Rule' : 'Save Changes'}
   606â†’        </Button>
   607â†’      </div>
   608â†’    </div>
   609â†’  );
   610â†’}

