     1â†’/**
     2â†’ * Routing Rule Editor Component
     3â†’ */
     4â†’
     5â†’import { useState, useEffect } from 'react';
     6â†’import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
     7â†’import { Button } from './ui/button';
     8â†’import { Input } from './ui/input';
     9â†’import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
    10â†’import {
    11â†’  Plus,
    12â†’  Save,
    13â†’  Trash2,
    14â†’  Copy,
    15â†’  ArrowUp,
    16â†’  ArrowDown,
    17â†’  TestTube,
    18â†’  Eye,
    19â†’  EyeOff
    20â†’} from 'lucide-react';
    21â†’import { useRoutingRules } from '../hooks/useApi';
    22â†’import {
    23â†’  RoutingRule,
    24â†’  RoutingCondition,
    25â†’  RoutingAction,
    26â†’  RoutingTestRequest,
    27â†’  RoutingTestResult
    28â†’} from '../types';
    29â†’import { toast } from 'react-hot-toast';
    30â†’
    31â†’interface RoutingRuleEditorProps {
    32â†’  rule?: RoutingRule;
    33â†’  onSave: (rule: RoutingRule) => void;
    34â†’  onCancel: () => void;
    35â†’  onTest: (request: RoutingTestRequest) => Promise<RoutingTestResult>;
    36â†’  isNew?: boolean;
    37â†’}
    38â†’
    39â†’export function RoutingRuleEditor({
    40â†’  rule,
    41â†’  onSave,
    42â†’  onCancel,
    43â†’  onTest,
    44â†’  isNew = false
    45â†’}: RoutingRuleEditorProps) {
    46â†’  const [currentRule, setCurrentRule] = useState<RoutingRule>(() =>
    47â†’    rule || createEmptyRule()
    48â†’  );
    49â†’  const [showTestRequest, setShowTestRequest] = useState(false);
    50â†’  const [testRequest, setTestRequest] = useState<RoutingTestRequest>({
    51â†’    model: 'gpt-4',
    52â†’    messages: [
    53â†’      { role: 'user', content: 'Hello, how are you?' }
    54â†’    ]
    55â†’  });
    56â†’  const [testResult, setTestResult] = useState<RoutingTestResult | null>(null);
    57â†’  const [testing, setTesting] = useState(false);
    58â†’
    59â†’  function createEmptyRule(): RoutingRule {
    60â†’    return {
    61â†’      id: `rule-${Date.now()}`,
    62â†’      name: '',
    63â†’      description: '',
    64â†’      enabled: true,
    65â†’      priority: 50,
    66â†’      conditions: [],
    67â†’      actions: [],
    68â†’      createdAt: Date.now(),
    69â†’      updatedAt: Date.now()
    70â†’    };
    71â†’  }
    72â†’
    73â†’  const addCondition = () => {
    74â†’    const newCondition: RoutingCondition = {
    75â†’      type: 'model',
    76â†’      operator: 'equals',
    77â†’      value: ''
    78â†’    };
    79â†’
    80â†’    setCurrentRule(prev => ({
    81â†’      ...prev,
    82â†’      conditions: [...prev.conditions, newCondition],
    83â†’      updatedAt: Date.now()
    84â†’    }));
    85â†’  };
    86â†’
    87â†’  const updateCondition = (index: number, condition: RoutingCondition) => {
    88â†’    setCurrentRule(prev => ({
    89â†’      ...prev,
    90â†’      conditions: prev.conditions.map((c, i) => i === index ? condition : c),
    91â†’      updatedAt: Date.now()
    92â†’    }));
    93â†’  };
    94â†’
    95â†’  const removeCondition = (index: number) => {
    96â†’    setCurrentRule(prev => ({
    97â†’      ...prev,
    98â†’      conditions: prev.conditions.filter((_, i) => i !== index),
    99â†’      updatedAt: Date.now()
   100â†’    }));
   101â†’  };
   102â†’
   103â†’  const moveCondition = (index: number, direction: 'up' | 'down') => {
   104â†’    const conditions = [...currentRule.conditions];
   105â†’    const targetIndex = direction === 'up' ? index - 1 : index + 1;
   106â†’
   107â†’    if (targetIndex >= 0 && targetIndex < conditions.length) {
   108â†’      [conditions[index], conditions[targetIndex]] = [conditions[targetIndex], conditions[index]];
   109â†’      setCurrentRule(prev => ({
   110â†’        ...prev,
   111â†’        conditions,
   112â†’        updatedAt: Date.now()
   113â†’      }));
   114â†’    }
   115â†’  };
   116â†’
   117â†’  const addAction = () => {
   118â†’    const newAction: RoutingAction = {
   119â†’      type: 'route_to',
   120â†’      value: ''
   121â†’    };
   122â†’
   123â†’    setCurrentRule(prev => ({
   124â†’      ...prev,
   125â†’      actions: [...prev.actions, newAction],
   126â†’      updatedAt: Date.now()
   127â†’    }));
   128â†’  };
   129â†’
   130â†’  const updateAction = (index: number, action: RoutingAction) => {
   131â†’    setCurrentRule(prev => ({
   132â†’      ...prev,
   133â†’      actions: prev.actions.map((a, i) => i === index ? action : a),
   134â†’      updatedAt: Date.now()
   135â†’    }));
   136â†’  };
   137â†’
   138â†’  const removeAction = (index: number) => {
   139â†’    setCurrentRule(prev => ({
   140â†’      ...prev,
   141â†’      actions: prev.actions.filter((_, i) => i !== index),
   142â†’      updatedAt: Date.now()
   143â†’    }));
   144â†’  };
   145â†’
   146â†’  const moveAction = (index: number, direction: 'up' | 'down') => {
   147â†’    const actions = [...currentRule.actions];
   148â†’    const targetIndex = direction === 'up' ? index - 1 : index + 1;
   149â†’
   150â†’    if (targetIndex >= 0 && targetIndex < actions.length) {
   151â†’      [actions[index], actions[targetIndex]] = [actions[targetIndex], actions[index]];
   152â†’      setCurrentRule(prev => ({
   153â†’        ...prev,
   154â†’        actions,
   155â†’        updatedAt: Date.now()
   156â†’      }));
   157â†’    }
   158â†’  };
   159â†’
   160â†’  const handleSave = () => {
