     1→/**
     2→ * RouteCodex Routing Management Component
     3→ */
     4→
     5→import { useState, useEffect } from 'react';
     6→import { useRoutingConfig, useRoutingRules, useRoutingInfo } from '../hooks/useApi';
     7→import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
     8→import { Button } from './ui/button';
     9→import { Input } from './ui/input';
    10→import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
    11→import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
    12→import {
    13→  Plus,
    14→  Edit,
    15→  Trash2,
    16→  Save,
    17→  X,
    18→  Play,
    19→  Settings,
    20→  Server,
    21→  ArrowRight,
    22→  CheckCircle,
    23→  AlertCircle,
    24→  Clock
    25→} from 'lucide-react';
    26→import { RoutingConfig, RoutingRule, RoutingStats } from '../types';
    27→
    28→export function RoutingManager() {
    29→  const [activeTab, setActiveTab] = useState('config');
    30→  const [editingRule, setEditingRule] = useState<RoutingRule | null>(null);
    31→  const [testRequest, setTestRequest] = useState({
    32→    model: '',
    33→    messages: [{ role: 'user', content: '' }],
    34→    endpoint: '/v1/chat/completions',
    35→    protocol: 'openai'
    36→  });
    37→
    38→  const {
    39→    getRoutingConfig,
    40→    updateRoutingConfig,
    41→    loading: configLoading
    42→  } = useRoutingConfig();
    43→
    44→  const {
    45→    getRoutingRules,
    46→    createRoutingRule,
    47→    updateRoutingRule,
    48→    deleteRoutingRule,
    49→    testRoutingRule
    50→  } = useRoutingRules();
    51→
    52→  const {
    53→    getProviders,
    54→    getStats
    55→  } = useRoutingInfo();
    56→
    57→  const [config, setConfig] = useState<RoutingConfig | null>(null);
    58→  const [rules, setRules] = useState<RoutingRule[]>([]);
    59→  const [stats, setStats] = useState<RoutingStats | null>(null);
    60→  const [testResult, setTestResult] = useState<any>(null);
    61→
    62→  useEffect(() => {
    63→    loadRoutingData();
    64→  }, []);
    65→
    66→  const loadRoutingData = async () => {
    67→    try {
    68→      const [configData, rulesData, _providersData, statsData] = await Promise.all([
    69→        getRoutingConfig(),
    70→        getRoutingRules(),
    71→        getProviders(),
    72→        getStats()
    73→      ]);
    74→
    75→      setConfig(configData);
    76→      setRules(rulesData);
    77→      setStats(statsData);
    78→    } catch (error) {
    79→      console.error('Failed to load routing data:', error);
    80→    }
    81→  };
    82→
    83→  const handleSaveConfig = async () => {
    84→    if (!config) return;
    85→
    86→    try {
    87→      await updateRoutingConfig(config);
    88→      alert('配置保存成功！');
    89→    } catch (error) {
    90→      alert('配置保存失败：' + (error as Error).message);
    91→    }
    92→  };
    93→
    94→  const handleCreateRule = async () => {
    95→    const newRule: RoutingRule = {
    96→      id: `rule-${Date.now()}`,
    97→      name: 'New Routing Rule',
    98→      description: 'New routing rule description',
    99→      enabled: true,
   100→      priority: 50,
   101→      conditions: [],
   102→      actions: [],
   103→      createdAt: Date.now(),
   104→      updatedAt: Date.now()
   105→    };
   106→
   107→    try {
   108→      await createRoutingRule(newRule);
   109→      loadRoutingData();
   110→      setEditingRule(newRule);
   111→    } catch (error) {
   112→      alert('创建路由规则失败：' + (error as Error).message);
   113→    }
   114→  };
   115→
   116→  const handleUpdateRule = async (rule: RoutingRule) => {
   117→    try {
   118→      await updateRoutingRule(rule.id, rule);
   119→      loadRoutingData();
   120→      setEditingRule(null);
   121→    } catch (error) {
   122→      alert('更新路由规则失败：' + (error as Error).message);
   123→    }
   124→  };
   125→
   126→  const handleDeleteRule = async (ruleId: string) => {
   127→    if (!confirm('确定要删除这个路由规则吗？')) return;
   128→
   129→    try {
   130→      await deleteRoutingRule(ruleId);
   131→      loadRoutingData();
   132→    } catch (error) {
   133→      alert('删除路由规则失败：' + (error as Error).message);
   134→    }
   135→  };
   136→
   137→  const handleTestRouting = async () => {
   138→    try {
   139→      const result = await testRoutingRule(testRequest);
   140→      setTestResult(result);
   141→    } catch (error) {
   142→      alert('路由测试失败：' + (error as Error).message);
   143→    }
   144→  };
   145→
   146→  const addCondition = (rule: RoutingRule) => {
   147→    const newCondition = {
   148→      type: 'model' as const,
   149→      operator: 'contains' as const,
   150→      value: '',
   151→      weight: 1
   152→    };
   153→    rule.conditions.push(newCondition);
   154→    setEditingRule({ ...rule });
   155→  };
   156→
   157→  const addAction = (rule: RoutingRule) => {
   158→    const newAction = {
   159→      type: 'route_to' as const,
   160→      value: ''
   161→    };
   162→    rule.actions.push(newAction);
   163→    setEditingRule({ ...rule });
   164→  };
   165→
   166→  const removeCondition = (rule: RoutingRule, index: number) => {
   167→    rule.conditions.splice(index, 1);
   168→    setEditingRule({ ...rule });
   169→  };
   170→
   171→  const removeAction = (rule: RoutingRule, index: number) => {
   172→    rule.actions.splice(index, 1);
   173→    setEditingRule({ ...rule });
   174→  };
   175→
   176→  return (
   177→    <div className="space-y-6">
   178→      <div className="flex items-center justify-between">
   179→        <div>
   180→          <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
   181→            动态路由管理
   182→          </h2>
   183→          <p className="text-gray-600 dark:text-gray-400 mt-1">
   184→            配置和管理智能路由规则，实现动态请求分发
   185→          </p>
   186→        </div>
   187→
   188→        <Button
   189→          onClick={loadRoutingData}
   190→          variant="outline"
   191→          className="flex items-center space-x-2"
   192→        >
   193→          <Settings className="w-4 h-4" />
   194→          <span>刷新数据</span>
   195→        </Button>
   196→      </div>
   197→
   198→      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4">
   199→        <TabsList className="grid w-full grid-cols-4">
   200→          <TabsTrigger value="config">配置管理</TabsTrigger>
   201→          <TabsTrigger value="rules">路由规则</TabsTrigger>
   202→          <TabsTrigger value="test">路由测试</TabsTrigger>
   203→          <TabsTrigger value="stats">统计分析</TabsTrigger>
   204→        </TabsList>
   205→
   206→        <TabsContent value="config" className="space-y-6">
   207→          <Card>
   208→            <CardHeader>
   209→              <CardTitle>路由配置</CardTitle>
   210→              <CardDescription>
   211→                管理全局路由配置和提供商设置
   212→              </CardDescription>
   213→            </CardHeader>
   214→            <CardContent>
   215→              {config ? (
   216→                <div className="space-y-4">
   217→                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
   218→                    <div className="space-y-2">
   219→                      <label className="text-sm font-medium">输入协议</label>
   220→                      <Select
   221→                        value={config.virtualrouter.inputProtocol}
   222→                        onValueChange={(value) =>
   223→                          setConfig({
   224→                            ...config,
   225→                            virtualrouter: {
   226→                              ...config.virtualrouter,
   227→                              inputProtocol: value
   228→                            }
   229→                          })
   230→                        }
   231→                      >
   232→                        <SelectTrigger>
   233→                          <SelectValue />
   234→                        </SelectTrigger>
   235→                        <SelectContent>
   236→                          <SelectItem value="openai">OpenAI</SelectItem>
   237→                          <SelectItem value="anthropic">Anthropic</SelectItem>
   238→                          <SelectItem value="custom">Custom</SelectItem>
   239→                        </SelectContent>
   240→                      </Select>
   241→                    </div>
   242→
   243→                    <div className="space-y-2">
   244→                      <label className="text-sm font-medium">输出协议</label>
   245→                      <Select
   246→                        value={config.virtualrouter.outputProtocol}
   247→                        onValueChange={(value) =>
   248→                          setConfig({
   249→                            ...config,
   250→                            virtualrouter: {
   251→                              ...config.virtualrouter,
   252→                              outputProtocol: value
   253→                            }
   254→                          })
   255→                        }
   256→                      >
   257→                        <SelectTrigger>
   258→                          <SelectValue />
   259→                        </SelectTrigger>
   260→                        <SelectContent>
   261→                          <SelectItem value="openai">OpenAI</SelectItem>
   262→                          <SelectItem value="anthropic">Anthropic</SelectItem>
   263→                          <SelectItem value="custom">Custom</SelectItem>
   264→                        </SelectContent>
   265→                      </Select>
   266→                    </div>
   267→                  </div>
   268→
   269→                  <div className="space-y-2">
   270→                    <label className="text-sm font-medium">HTTP服务器端口</label>
   271→                    <Input
   272→                      type="number"
   273→                      value={config.httpserver.port}
   274→                      onChange={(e) =>
   275→                        setConfig({
   276→                          ...config,
   277→                          httpserver: {
   278→                            ...config.httpserver,
   279→                            port: parseInt(e.target.value)
   280→                          }
   281→                        })
   282→                      }
   283→                    />
   284→                  </div>
   285→
   286→                  <div className="space-y-2">
   287→                    <label className="text-sm font-medium">HTTP服务器主机</label>
   288→                    <Input
   289→                      value={config.httpserver.host}
   290→                      onChange={(e) =>
   291→                        setConfig({
   292→                          ...config,
   293→                          httpserver: {
   294→                            ...config.httpserver,
   295→                            host: e.target.value
   296→                          }
   297→                        })
   298→                      }
   299→                    />
   300→                  </div>
   301→
   302→                  <Button onClick={handleSaveConfig} className="flex items-center space-x-2">
   303→                    <Save className="w-4 h-4" />
   304→                    <span>保存配置</span>
   305→                  </Button>
   306→                </div>
   307→              ) : (
   308→                <div className="text-center py-8 text-gray-500">
   309→                  {configLoading ? '加载中...' : '无配置数据'}
   310→                </div>
   311→              )}
   312→            </CardContent>
   313→          </Card>
   314→        </TabsContent>
   315→
   316→        <TabsContent value="rules" className="space-y-6">
   317→          <div className="flex items-center justify-between">
   318→            <h3 className="text-lg font-semibold">路由规则管理</h3>
   319→            <Button onClick={handleCreateRule} className="flex items-center space-x-2">
   320→              <Plus className="w-4 h-4" />
   321→              <span>新建规则</span>
   322→            </Button>
   323→          </div>
   324→
   325→          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
   326→            {rules.map((rule) => (
   327→              <Card key={rule.id}>
   328→                <CardHeader>
   329→                  <div className="flex items-center justify-between">
   330→                    <div>
   331→                      <CardTitle className="text-base">{rule.name}</CardTitle>
   332→                      <CardDescription>{rule.description}</CardDescription>
   333→                    </div>
   334→                    <div className="flex items-center space-x-2">
   335→                      <Button
   336→                        variant="outline"
   337→                        size="sm"
   338→                        onClick={() => setEditingRule(rule)}
   339→                      >
   340→                        <Edit className="w-4 h-4" />
   341→                      </Button>
   342→                      <Button
   343→                        variant="outline"
   344→                        size="sm"
   345→                        onClick={() => handleDeleteRule(rule.id)}
   346→                      >
   347→                        <Trash2 className="w-4 h-4" />
   348→                      </Button>
   349→                    </div>
   350→                  </div>
   351→                </CardHeader>
   352→                <CardContent>
   353→                  <div className="space-y-3">
   354→                    <div className="flex items-center space-x-4">
   355→                      <div className="flex items-center space-x-1">
   356→                        {rule.enabled ? (
   357→                          <CheckCircle className="w-4 h-4 text-green-500" />
   358→                        ) : (
   359→                          <AlertCircle className="w-4 h-4 text-gray-400" />
   360→                        )}
   361→                        <span className="text-sm">{rule.enabled ? '启用' : '禁用'}</span>
   362→                      </div>
   363→                      <div className="text-sm text-gray-500">
   364→                        优先级: {rule.priority}
   365→                      </div>
   366→                      <div className="text-sm text-gray-500">
   367→                        条件: {rule.conditions.length}
   368→                      </div>
   369→                    </div>
   370→
   371→                    {rule.conditions.length > 0 && (
   372→                      <div className="space-y-1">
   373→                        <div className="text-sm font-medium">条件:</div>
   374→                        {rule.conditions.map((condition, index) => (
   375→                          <div key={index} className="text-sm text-gray-600 bg-gray-50 dark:bg-gray-800 p-2 rounded">
   376→                            {condition.type} {condition.operator} "{condition.value}"
   377→                          </div>
   378→                        ))}
   379→                      </div>
   380→                    )}
   381→
   382→                    {rule.actions.length > 0 && (
   383→                      <div className="space-y-1">
   384→                        <div className="text-sm font-medium">动作:</div>
   385→                        {rule.actions.map((action, index) => (
   386→                          <div key={index} className="text-sm text-gray-600 bg-gray-50 dark:bg-gray-800 p-2 rounded">
   387→                            {action.type}: {typeof action.value === 'string' ? action.value : JSON.stringify(action.value)}
   388→                          </div>
   389→                        ))}
   390→                      </div>
   391→                    )}
   392→                  </div>
   393→                </CardContent>
   394→              </Card>
   395→            ))}
   396→          </div>
   397→
   398→          {editingRule && (
   399→            <Card>
   400→              <CardHeader>
   401→                <div className="flex items-center justify-between">
   402→                  <CardTitle>编辑路由规则</CardTitle>
   403→                  <Button
   404→                    variant="outline"
   405→                    size="sm"
   406→                    onClick={() => setEditingRule(null)}
   407→                  >
   408→                    <X className="w-4 h-4" />
   409→                  </Button>
   410→                </div>
   411→              </CardHeader>
   412→              <CardContent>
   413→                <div className="space-y-4">
   414→                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
   415→                    <div className="space-y-2">
   416→                      <label className="text-sm font-medium">规则名称</label>
   417→                      <Input
   418→                        value={editingRule.name}
   419→                        onChange={(e) =>
   420→                          setEditingRule({ ...editingRule, name: e.target.value })
   421→                        }
   422→                      />
   423→                    </div>
   424→                    <div className="space-y-2">
   425→                      <label className="text-sm font-medium">优先级</label>
   426→                      <Input
   427→                        type="number"
   428→                        value={editingRule.priority}
   429→                        onChange={(e) =>
   430→                          setEditingRule({
   431→                            ...editingRule,
   432→                            priority: parseInt(e.target.value)
   433→                          })
   434→                        }
   435→                      />
   436→                    </div>
   437→                  </div>
   438→
   439→                  <div className="space-y-2">
   440→                    <label className="text-sm font-medium">描述</label>
   441→                    <Input
   442→                      value={editingRule.description}
   443→                      onChange={(e) =>
   444→                        setEditingRule({ ...editingRule, description: e.target.value })
   445→                      }
   446→                    />
   447→                  </div>
   448→
   449→                  <div className="flex items-center space-x-2">
   450→                    <input
   451→                      type="checkbox"
   452→                      checked={editingRule.enabled}
   453→                      onChange={(e) =>
   454→                        setEditingRule({ ...editingRule, enabled: e.target.checked })
   455→                      }
   456→                    />
   457→                    <label className="text-sm">启用规则</label>
   458→                  </div>
   459→
   460→                  <div className="space-y-2">
   461→                    <div className="flex items-center justify-between">
   462→                      <label className="text-sm font-medium">条件</label>
   463→                      <Button
   464→                        variant="outline"
   465→                        size="sm"
   466→                        onClick={() => addCondition(editingRule)}
   467→                      >
   468→                        <Plus className="w-4 h-4" />
   469→                      </Button>
   470→                    </div>
   471→                    {editingRule.conditions.map((condition, index) => (
   472→                      <div key={index} className="grid grid-cols-12 gap-2 items-center">
   473→                        <Select
   474→                          value={condition.type}
   475→                          onValueChange={(value: any) => {
   476→                            condition.type = value;
   477→                            setEditingRule({ ...editingRule });
   478→                          }}
   479→                        >
   480→                          <SelectTrigger className="col-span-3">
   481→                            <SelectValue />
   482→                          </SelectTrigger>
   483→                          <SelectContent>
   484→                            <SelectItem value="model">模型</SelectItem>
   485→                            <SelectItem value="token_count">令牌数量</SelectItem>
   486→                            <SelectItem value="content_type">内容类型</SelectItem>
   487→                            <SelectItem value="tool_type">工具类型</SelectItem>
   488→                            <SelectItem value="custom">自定义</SelectItem>
   489→                          </SelectContent>
   490→                        </Select>
   491→
   492→                        <Select
   493→                          value={condition.operator}
   494→                          onValueChange={(value: any) => {
   495→                            condition.operator = value;
   496→                            setEditingRule({ ...editingRule });
   497→                          }}
   498→                        >
   499→                          <SelectTrigger className="col-span-3">
   500→                            <SelectValue />
   501→                          </SelectTrigger>
   502→                          <SelectContent>
   503→                            <SelectItem value="equals">等于</SelectItem>
   504→                            <SelectItem value="not_equals">不等于</SelectItem>
   505→                            <SelectItem value="contains">包含</SelectItem>
   506→                            <SelectItem value="not_contains">不包含</SelectItem>
   507→                            <SelectItem value="greater_than">大于</SelectItem>
   508→                            <SelectItem value="less_than">小于</SelectItem>
   509→                          </SelectContent>
   510→                        </Select>
   511→
   512→                        <Input
   513→                          className="col-span-4"
   514→                          value={condition.value as string}
   515→                          onChange={(e) => {
   516→                            condition.value = e.target.value;
   517→                            setEditingRule({ ...editingRule });
   518→                          }}
   519→                        />
   520→
   521→                        <Button
   522→                          variant="outline"
   523→                          size="sm"
   524→                          onClick={() => removeCondition(editingRule, index)}
   525→                        >
   526→                          <X className="w-4 h-4" />
   527→                        </Button>
   528→                      </div>
   529→                    ))}
   530→                  </div>
   531→
   532→                  <div className="space-y-2">
   533→                    <div className="flex items-center justify-between">
   534→                      <label className="text-sm font-medium">动作</label>
   535→                      <Button
   536→                        variant="outline"
   537→                        size="sm"
   538→                        onClick={() => addAction(editingRule)}
   539→                      >
   540→                        <Plus className="w-4 h-4" />
   541→                      </Button>
   542→                    </div>
   543→                    {editingRule.actions.map((action, index) => (
   544→                      <div key={index} className="grid grid-cols-12 gap-2 items-center">
   545→                        <Select
   546→                          value={action.type}
   547→                          onValueChange={(value: any) => {
   548→                            action.type = value;
   549→                            setEditingRule({ ...editingRule });
   550→                          }}
   551→                        >
   552→                          <SelectTrigger className="col-span-4">
   553→                            <SelectValue />
   554→                          </SelectTrigger>
   555→                          <SelectContent>
   556→                            <SelectItem value="route_to">路由到</SelectItem>
   557→                            <SelectItem value="modify_request">修改请求</SelectItem>
   558→                            <SelectItem value="add_header">添加头部</SelectItem>
   559→                            <SelectItem value="set_param">设置参数</SelectItem>
   560→                            <SelectItem value="log">日志记录</SelectItem>
   561→                          </SelectContent>
   562→                        </Select>
   563→
   564→                        <Input
   565→                          className="col-span-6"
   566→                          value={typeof action.value === 'string' ? action.value : JSON.stringify(action.value)}
   567→                          onChange={(e) => {
   568→                            try {
   569→                              action.value = JSON.parse(e.target.value);
   570→                            } catch {
   571→                              action.value = e.target.value;
   572→                            }
   573→                            setEditingRule({ ...editingRule });
   574→                          }}
   575→                        />
   576→
   577→                        <Button
   578→                          variant="outline"
   579→                          size="sm"
   580→                          onClick={() => removeAction(editingRule, index)}
   581→                        >
   582→                          <X className="w-4 h-4" />
   583→                        </Button>
   584→                      </div>
   585→                    ))}
   586→                  </div>
   587→
   588→                  <div className="flex space-x-2">
   589→                    <Button onClick={() => handleUpdateRule(editingRule)}>
   590→                      保存规则
   591→                    </Button>
   592→                    <Button
   593→                      variant="outline"
   594→                      onClick={() => setEditingRule(null)}
   595→                    >
   596→                      取消
   597→                    </Button>
   598→                  </div>
   599→                </div>
   600→              </CardContent>
   601→            </Card>
   602→          )}
   603→        </TabsContent>
   604→
   605→        <TabsContent value="test" className="space-y-6">
   606→          <Card>
   607→            <CardHeader>
   608→              <CardTitle>路由测试</CardTitle>
   609→              <CardDescription>
   610→                测试路由规则匹配和执行效果
   611→              </CardDescription>
   612→            </CardHeader>
   613→            <CardContent>
   614→              <div className="space-y-4">
   615→                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
   616→                  <div className="space-y-2">
   617→                    <label className="text-sm font-medium">模型名称</label>
   618→                    <Input
   619→                      value={testRequest.model}
   620→                      onChange={(e) =>
   621→                        setTestRequest({ ...testRequest, model: e.target.value })
   622→                      }
