   687→            </CardContent>
   688→          </Card>
   689→        </TabsContent>
   690→
   691→        <TabsContent value="stats" className="space-y-6">
   692→          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
   693→            <Card>
