   742→                      <div key={route} className="flex justify-between items-center">
   743→                        <span className="text-sm">{route}</span>
   744→                        <span className="text-sm font-medium">{count as number}</span>
   745→                      </div>
   746→                    ))}
   747→                  </div>
   748→                ) : (
   749→                  <div className="text-center py-4 text-gray-500">无数据</div>
   750→                )}
   751→              </CardContent>
   752→            </Card>
   753→
   754→            <Card>
   755→              <CardHeader>
   756→                <CardTitle>提供商使用统计</CardTitle>
   757→                <CardDescription>各提供商的使用频率</CardDescription>
   758→              </CardHeader>
   759→              <CardContent>
   760→                {stats?.providerUsage ? (
   761→                  <div className="space-y-2">
   762→                    {Object.entries(stats.providerUsage).map(([provider, count]) => (
   763→                      <div key={provider} className="flex justify-between items-center">
   764→                        <span className="text-sm">{provider}</span>
   765→                        <span className="text-sm font-medium">{count as number}</span>
   766→                      </div>
   767→                    ))}
   768→                  </div>
   769→                ) : (
   770→                  <div className="text-center py-4 text-gray-500">无数据</div>
   771→                )}
   772→              </CardContent>
   773→            </Card>
   774→          </div>
   775→
   776→          <Card>
   777→            <CardHeader>
   778→              <CardTitle>错误统计</CardTitle>
   779→              <CardDescription>系统错误类型和频率</CardDescription>
   780→            </CardHeader>
   781→            <CardContent>
   782→              {stats?.errors && stats.errors.length > 0 ? (
   783→                <div className="space-y-2">
   784→                  {stats.errors.map((error, index) => (
   785→                    <div key={index} className="flex justify-between items-center p-2 bg-red-50 dark:bg-red-900/20 rounded">
   786→                      <div>
   787→                        <span className="text-sm font-medium">{error.type}</span>
   788→                        <span className="text-sm text-gray-500 ml-2">
   789→                          {new Date(error.lastOccurred).toLocaleString()}
   790→                        </span>
   791→                      </div>
   792→                      <span className="text-sm font-medium">{error.count}</span>
   793→                    </div>
   794→                  ))}
   795→                </div>
   796→              ) : (
   797→                <div className="text-center py-4 text-gray-500">无错误数据</div>
   798→              )}
   799→            </CardContent>
   800→          </Card>
   801→        </TabsContent>
   802→      </Tabs>
   803→    </div>
   804→  );
   805→}

