     5→import { useState, useEffect } from 'react';
     6→import { useRoutingConfig, useRoutingRules, useRoutingInfo } from '../hooks/useApi';
     7→import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
