    10→import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
    11→import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
    12→import {
    13→  Plus,
    14→  Edit,
    15→  Trash2,
    16→  Save,
    17→  X,
    18→  Play,
    19→  Settings,
    20→  Server,
    21→  ArrowRight,
