    26→import { RoutingConfig, RoutingRule, RoutingStats } from '../types';
    27→
    28→export function RoutingManager() {
    29→  const [activeTab, setActiveTab] = useState('config');
    30→  const [editingRule, setEditingRule] = useState<RoutingRule | null>(null);
    31→  const [testRequest, setTestRequest] = useState({
    32→    model: '',
    33→    messages: [{ role: 'user', content: '' }],
    34→    endpoint: '/v1/chat/completions',
    35→    protocol: 'openai'
    36→  });
    37→
    38→  const {
    39→    getRoutingConfig,
    40→    updateRoutingConfig,
    41→    loading: configLoading
    42→  } = useRoutingConfig();
    43→
    44→  const {
    45→    getRoutingRules,
    46→    createRoutingRule,
    47→    updateRoutingRule,
    48→    deleteRoutingRule,
    49→    testRoutingRule
    50→  } = useRoutingRules();
    51→
    52→  const {
    53→    getProviders,
    54→    getStats
