    56→
    57→  const [config, setConfig] = useState<RoutingConfig | null>(null);
    58→  const [rules, setRules] = useState<RoutingRule[]>([]);
    59→  const [stats, setStats] = useState<RoutingStats | null>(null);
    60→  const [testResult, setTestResult] = useState<any>(null);
    61→
    62→  useEffect(() => {
    63→    loadRoutingData();
    64→  }, []);
    65→
    66→  const loadRoutingData = async () => {
    67→    try {
    68→      const [configData, rulesData, _providersData, statsData] = await Promise.all([
    69→        getRoutingConfig(),
    70→        getRoutingRules(),
    71→        getProviders(),
    72→        getStats()
    73→      ]);
    74→
    75→      setConfig(configData);
    76→      setRules(rulesData);
    77→      setStats(statsData);
    78→    } catch (error) {
    79→      console.error('Failed to load routing data:', error);
    80→    }
    81→  };
    82→
    83→  const handleSaveConfig = async () => {
    84→    if (!config) return;
    85→
    86→    try {
    87→      await updateRoutingConfig(config);
    88→      alert('配置保存成功！');
    89→    } catch (error) {
    90→      alert('配置保存失败：' + (error as Error).message);
    91→    }
    92→  };
    93→
    94→  const handleCreateRule = async () => {
    95→    const newRule: RoutingRule = {
    96→      id: `rule-${Date.now()}`,
    97→      name: 'New Routing Rule',
    98→      description: 'New routing rule description',
    99→      enabled: true,
   100→      priority: 50,
   101→      conditions: [],
   102→      actions: [],
   103→      createdAt: Date.now(),
   104→      updatedAt: Date.now()
   105→    };
   106→
   107→    try {
   108→      await createRoutingRule(newRule);
   109→      loadRoutingData();
   110→      setEditingRule(newRule);
   111→    } catch (error) {
   112→      alert('创建路由规则失败：' + (error as Error).message);
   113→    }
   114→  };
   115→
   116→  const handleUpdateRule = async (rule: RoutingRule) => {
