   148→      type: 'model' as const,
   149→      operator: 'contains' as const,
   150→      value: '',
   151→      weight: 1
   152→    };
   153→    rule.conditions.push(newCondition);
   154→    setEditingRule({ ...rule });
   155→  };
   156→
   157→  const addAction = (rule: RoutingRule) => {
   158→    const newAction = {
   159→      type: 'route_to' as const,
   160→      value: ''
   161→    };
   162→    rule.actions.push(newAction);
   163→    setEditingRule({ ...rule });
   164→  };
   165→
   166→  const removeCondition = (rule: RoutingRule, index: number) => {
   167→    rule.conditions.splice(index, 1);
   168→    setEditingRule({ ...rule });
   169→  };
   170→
   171→  const removeAction = (rule: RoutingRule, index: number) => {
   172→    rule.actions.splice(index, 1);
   173→    setEditingRule({ ...rule });
   174→  };
   175→
   176→  return (
   177→    <div className="space-y-6">
   178→      <div className="flex items-center justify-between">
   179→        <div>
