/**
 * Backend Service Auto-Manager
 * 自动检测和管理RouteCodex后端服务
 */

import { spawn, ChildProcess } from 'child_process';
import { EventEmitter } from 'events';

export interface BackendStatus {
  isRunning: boolean;
  isStarting: boolean;
  port: number;
  health?: any;
  error?: string;
  lastCheck: Date;
}

export class BackendServiceManager extends EventEmitter {
  private static instance: BackendServiceManager;
  private backendProcess: ChildProcess | null = null;
  private isStarting = false;
  private status: BackendStatus = {
    isRunning: false,
    isStarting: false,
    port: 5506,
    lastCheck: new Date()
  };
  private healthCheckInterval: NodeJS.Timeout | null = null;
  private readonly HEALTH_CHECK_INTERVAL = 5000; // 5秒检查一次

  private constructor() {
    super();
    this.startHealthCheck();
  }

  static getInstance(): BackendServiceManager {
    if (!BackendServiceManager.instance) {
      BackendServiceManager.instance = new BackendServiceManager();
    }
    return BackendServiceManager.instance;
  }

  /**
   * 确保后端服务正在运行
   */
  async ensureBackendRunning(): Promise<boolean> {
    console.log('🔍 检查后端服务状态...');

    // 1. 检测后端是否已运行
    const isHealthy = await this.checkBackendHealth();
    if (isHealthy) {
      console.log('✅ 后端服务已运行');
      return true;
    }

    // 2. 自动启动后端
    if (!this.isStarting) {
      console.log('🚀 启动后端服务...');
      return await this.startBackend();
    }

    // 3. 正在启动中，等待结果
    console.log('⏳ 后端服务启动中...');
    return await this.waitForStartup();
  }

  /**
   * 检查后端健康状态
   */
  private async checkBackendHealth(): Promise<boolean> {
    try {
      const response = await fetch('http://localhost:5506/health', {
        method: 'GET',
        timeout: 3000
      });

      if (response.ok) {
        const health = await response.json();
        this.updateStatus({
          isRunning: true,
          isStarting: false,
          health,
          error: undefined,
          lastCheck: new Date()
        });
        return true;
      }
    } catch (error) {
      this.updateStatus({
        isRunning: false,
        isStarting: this.isStarting,
        error: (error as Error).message,
        lastCheck: new Date()
      });
    }
    return false;
  }

  /**
   * 启动后端服务
   */
  private async startBackend(): Promise<boolean> {
    if (this.isStarting || this.backendProcess) {
      return await this.waitForStartup();
    }

    this.isStarting = true;
    this.updateStatus({
      isStarting: true,
      lastCheck: new Date()
    });

    try {
      // 查找RouteCodex配置文件
      const configPath = await this.findConfigFile();
      if (!configPath) {
        throw new Error('未找到RouteCodex配置文件');
      }

      console.log(`📁 使用配置文件: ${configPath}`);

      // 启动RouteCodex后端
      const args = [
        'start',
        '--config', configPath,
        '--port', '5506'
      ];

      this.backendProcess = spawn('rcc4', args, {
        stdio: ['pipe', 'pipe', 'pipe'],
        env: { ...process.env }
      });

      this.setupProcessEventHandlers();

      // 等待启动完成
      const success = await this.waitForStartup();

      if (success) {
        console.log('✅ 后端服务启动成功');
      } else {
        console.log('❌ 后端服务启动失败');
      }

      return success;

    } catch (error) {
      console.error('❌ 启动后端服务失败:', error);
      this.updateStatus({
        isStarting: false,
        error: (error as Error).message,
        lastCheck: new Date()
      });
      return false;
    }
  }

  /**
   * 等待后端启动完成
   */
  private async waitForStartup(timeout = 30000): Promise<boolean> {
    const startTime = Date.now();

    while (Date.now() - startTime < timeout) {
      if (await this.checkBackendHealth()) {
        this.isStarting = false;
        this.updateStatus({
          isStarting: false,
          lastCheck: new Date()
        });
        return true;
      }

      if (!this.isStarting && !this.backendProcess) {
        // 启动过程中断了
        return false;
      }

      await new Promise(resolve => setTimeout(resolve, 1000));
    }

    // 超时
    this.isStarting = false;
    if (this.backendProcess) {
      this.backendProcess.kill();
      this.backendProcess = null;
    }
    return false;
  }

  /**
   * 查找配置文件
   */
  private async findConfigFile(): Promise<string | null> {
    const possiblePaths = [
      `${process.env.HOME}/.route-claudecode/config/v4/single-provider/lmstudio-v4-5506.json`,
      './config/default.json',
      './config/rcc4-config.json'
    ];

    for (const path of possiblePaths) {
      try {
        const fs = await import('fs/promises');
        await fs.access(path);
        return path;
      } catch {
        continue;
      }
    }

    return null;
  }

  /**
   * 设置进程事件处理器
   */
  private setupProcessEventHandlers() {
    if (!this.backendProcess) return;

    this.backendProcess.stdout?.on('data', (data) => {
      const output = data.toString().trim();
      if (output) {
        console.log(`[RouteCodex] ${output}`);
      }
    });

    this.backendProcess.stderr?.on('data', (data) => {
      const output = data.toString().trim();
      if (output) {
        console.error(`[RouteCodex Error] ${output}`);
      }
    });

    this.backendProcess.on('exit', (code, signal) => {
      console.log(`RouteCodex 进程退出: code=${code}, signal=${signal}`);
      this.backendProcess = null;
      this.isStarting = false;
      this.updateStatus({
        isRunning: false,
        isStarting: false,
        error: `进程退出: code=${code}, signal=${signal}`,
        lastCheck: new Date()
      });
    });
  }

  /**
   * 启动健康检查定时器
   */
  private startHealthCheck() {
    this.healthCheckInterval = setInterval(async () => {
      if (this.status.isRunning || this.status.isStarting) {
        await this.checkBackendHealth();
      }
    }, this.HEALTH_CHECK_INTERVAL);
  }

  /**
   * 更新状态并发出事件
   */
  private updateStatus(newStatus: Partial<BackendStatus>) {
    this.status = { ...this.status, ...newStatus };
    this.emit('statusChanged', this.status);
  }

  /**
   * 获取当前状态
   */
  getStatus(): BackendStatus {
    return { ...this.status };
  }

  /**
   * 停止后端服务
   */
  async stopBackend(): Promise<boolean> {
    if (this.backendProcess) {
      this.backendProcess.kill('SIGTERM');

      // 等待进程退出
      await new Promise(resolve => {
        if (this.backendProcess) {
          this.backendProcess.on('exit', resolve);
        } else {
          resolve(undefined);
        }
      });

      this.backendProcess = null;
    }

    this.updateStatus({
      isRunning: false,
      isStarting: false,
      lastCheck: new Date()
    });

    return true;
  }

  /**
   * 清理资源
   */
  async cleanup() {
    if (this.healthCheckInterval) {
      clearInterval(this.healthCheckInterval);
      this.healthCheckInterval = null;
    }

    await this.stopBackend();
    this.removeAllListeners();
  }
}

// 导出单例实例
export const backendManager = BackendServiceManager.getInstance();

/**
 * Backend Service Status Indicator
 * 后端服务状态指示器组件
 */

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import { Badge } from './ui/badge';
import { backendManager, BackendStatus } from '../services/backendService';
import {
  CheckCircle,
  XCircle,
  AlertTriangle,
  Loader2,
  Server,
  Play,
  Square,
  RefreshCw
} from 'lucide-react';

interface BackendStatusIndicatorProps {
  onStatusChange?: (status: BackendStatus) => void;
}

export function BackendStatusIndicator({ onStatusChange }: BackendStatusIndicatorProps) {
  const [status, setStatus] = useState<BackendStatus>(backendManager.getStatus());
  const [isActionInProgress, setIsActionInProgress] = useState(false);

  useEffect(() => {
    // 监听状态变化
    const handleStatusChange = (newStatus: BackendStatus) => {
      setStatus(newStatus);
      onStatusChange?.(newStatus);
    };

    backendManager.on('statusChanged', handleStatusChange);

    // 组件挂载时确保后端运行
    ensureBackend();

    return () => {
      backendManager.off('statusChanged', handleStatusChange);
    };
  }, [onStatusChange]);

  const ensureBackend = async () => {
    setIsActionInProgress(true);
    try {
      await backendManager.ensureBackendRunning();
    } finally {
      setIsActionInProgress(false);
    }
  };

  const handleStartStop = async () => {
    setIsActionInProgress(true);
    try {
      if (status.isRunning) {
        await backendManager.stopBackend();
      } else {
        await backendManager.ensureBackendRunning();
      }
    } finally {
      setIsActionInProgress(false);
    }
  };

  const handleRefresh = async () => {
    setIsActionInProgress(true);
    try {
      await backendManager.checkBackendHealth();
    } finally {
      setIsActionInProgress(false);
    }
  };

  const getStatusIcon = () => {
    if (status.isStarting) {
      return <Loader2 className="w-5 h-5 animate-spin text-blue-500" />;
    }
    if (status.isRunning) {
      return <CheckCircle className="w-5 h-5 text-green-500" />;
    }
    return <XCircle className="w-5 h-5 text-red-500" />;
  };

  const getStatusBadge = () => {
    if (status.isStarting) {
      return <Badge variant="secondary" className="bg-blue-100 text-blue-800">启动中</Badge>;
    }
    if (status.isRunning) {
      return <Badge variant="default" className="bg-green-100 text-green-800">运行中</Badge>;
    }
    return <Badge variant="destructive">已停止</Badge>;
  };

  const getStatusText = () => {
    if (status.isStarting) return '后端服务启动中...';
    if (status.isRunning) return '后端服务正常运行';
    return '后端服务未运行';
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Server className="w-5 h-5" />
            <div>
              <CardTitle className="text-base">后端服务状态</CardTitle>
              <CardDescription>
                RouteCodex 路由服务器 ({status.port})
              </CardDescription>
            </div>
          </div>
          {getStatusBadge()}
        </div>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {/* 状态显示 */}
          <div className="flex items-center space-x-3">
            {getStatusIcon()}
            <div className="flex-1">
              <div className="font-medium">{getStatusText()}</div>
              <div className="text-sm text-gray-500">
                最后检查: {status.lastCheck.toLocaleTimeString()}
              </div>
            </div>
          </div>

          {/* 错误信息 */}
          {status.error && (
            <div className="flex items-center space-x-2 p-3 bg-red-50 dark:bg-red-900/20 rounded-lg">
              <AlertTriangle className="w-4 h-4 text-red-500 flex-shrink-0" />
              <div className="text-sm text-red-700 dark:text-red-300">
                {status.error}
              </div>
            </div>
          )}

          {/* 健康信息 */}
          {status.health && (
            <div className="space-y-2">
              <div className="text-sm font-medium">健康信息:</div>
              <div className="grid grid-cols-2 gap-2 text-sm">
                <div className="flex justify-between">
                  <span>评分:</span>
                  <span>{status.health.score}%</span>
                </div>
                <div className="flex justify-between">
                  <span>运行时间:</span>
                  <span>{status.health.uptime}s</span>
                </div>
                <div className="flex justify-between">
                  <span>响应时间:</span>
                  <span>{status.health.performance?.avgResponseTime?.toFixed(2)}ms</span>
                </div>
                <div className="flex justify-between">
                  <span>吞吐量:</span>
                  <span>{status.health.performance?.throughput} req/s</span>
                </div>
              </div>
            </div>
          )}

          {/* 操作按钮 */}
          <div className="flex items-center space-x-2">
            <Button
              onClick={handleStartStop}
              disabled={isActionInProgress || status.isStarting}
              variant={status.isRunning ? "destructive" : "default"}
              size="sm"
              className="flex items-center space-x-2"
            >
              {isActionInProgress || status.isStarting ? (
                <Loader2 className="w-4 h-4 animate-spin" />
              ) : status.isRunning ? (
                <Square className="w-4 h-4" />
              ) : (
                <Play className="w-4 h-4" />
              )}
              <span>
                {isActionInProgress || status.isStarting ?
                  (status.isStarting ? '启动中...' : '处理中...') :
                  (status.isRunning ? '停止服务' : '启动服务')
                }
              </span>
            </Button>

            <Button
              onClick={handleRefresh}
              disabled={isActionInProgress}
              variant="outline"
              size="sm"
              className="flex items-center space-x-2"
            >
              <RefreshCw className={`w-4 h-4 ${isActionInProgress ? 'animate-spin' : ''}`} />
              <span>刷新</span>
            </Button>
          </div>

          {/* 快速访问链接 */}
          {status.isRunning && (
            <div className="pt-2 border-t">
              <div className="text-sm font-medium mb-2">快速访问:</div>
              <div className="flex flex-wrap gap-2">
                <Badge variant="outline" className="text-xs">
                  <a
                    href="http://localhost:5506/health"
                    target="_blank"
                    rel="noopener noreferrer"
                    className="hover:underline"
                  >
                    健康检查
                  </a>
                </Badge>
                <Badge variant="outline" className="text-xs">
                  <a
                    href="http://localhost:5506/docs"
                    target="_blank"
                    rel="noopener noreferrer"
                    className="hover:underline"
                  >
                    API文档
                  </a>
                </Badge>
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

