              <Card>
                <CardHeader className="pb-2">
                  <CardTitle className="text-sm font-medium">总响应数</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold">{protocolStats.totalResponses}</div>
                </CardContent>
