  enabled: boolean;
  inputProtocol: string;
  outputProtocol: string;
  targetUrl: string;
  recordEnabled: boolean;
  analysisEnabled: boolean;
  transformEnabled: boolean;
}

export function ProtocolPassthrough() {
  const [config, setConfig] = useState<PassthroughConfig>({
    enabled: false,
    inputProtocol: 'openai',
    outputProtocol: 'openai',
    targetUrl: 'http://localhost:5506',
    recordEnabled: true,
    analysisEnabled: true,
    transformEnabled: false
  });

  const [isRunning, setIsRunning] = useState(false);
  const [testRequest, setTestRequest] = useState({
    model: 'gpt-3.5-turbo',
    messages: [
      { role: 'user', content: 'Hello, how are you?' }
    ],
    temperature: 0.7,
    max_tokens: 150
  });

  const [testResponse, setTestResponse] = useState<any>(null);
  const [requestAnalysis, setRequestAnalysis] = useState<any>(null);
  const [responseAnalysis, setResponseAnalysis] = useState<any>(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [showRawData, setShowRawData] = useState(false);

  useEffect(() => {
    // 这里可以连接WebSocket进行实时监控
    setupWebSocketConnection();
  }, []);

  const setupWebSocketConnection = () => {
    // 设置WebSocket连接来接收实时协议数据
    // 这里是模拟实现
  };

  const handleToggle = () => {
    setIsRunning(!isRunning);
    // 这里应该启动/停止透传服务
  };

  const handleTestRequest = async () => {
    setIsProcessing(true);
    setTestResponse(null);
    setRequestAnalysis(null);
    setResponseAnalysis(null);

    try {
      // 分析请求
      if (config.analysisEnabled) {
        const reqAnalysis = protocolAnalyzer.analyzeRequest(testRequest, config.inputProtocol, {
          route: 'test',
          provider: 'test-provider'
        });
        setRequestAnalysis(reqAnalysis);
      }

      // 发送请求到目标
      const response = await fetch(`${config.targetUrl}/v1/chat/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(testRequest)
      });

      const responseData = await response.json();
      setTestResponse(responseData);

      // 分析响应
      if (config.analysisEnabled) {
        const respAnalysis = protocolAnalyzer.analyzeResponse(responseData, config.outputProtocol, {
          route: 'test',
          provider: 'test-provider'
        });
        setResponseAnalysis(respAnalysis);
      }

    } catch (error) {
      console.error('Test request failed:', error);
      setTestResponse({ error: (error as Error).message });
    } finally {
      setIsProcessing(false);
    }
  };

  const handleCopyToClipboard = (data: any) => {
    navigator.clipboard.writeText(JSON.stringify(data, null, 2));
  };

  const handleExportTest = () => {
    const testData = {
      config,
      request: testRequest,
      response: testResponse,
      requestAnalysis,
      responseAnalysis,
      timestamp: new Date().toISOString()
    };

    const blob = new Blob([JSON.stringify(testData, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `protocol-test-${Date.now()}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
            协议透传测试
          </h2>
          <p className="text-gray-600 dark:text-gray-400 mt-1">
            测试协议透传、记录IO和分析字段结构
          </p>
        </div>

        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <div className={`w-2 h-2 rounded-full ${isRunning ? 'bg-green-500' : 'bg-gray-400'}`} />
            <span className="text-sm text-gray-600 dark:text-gray-400">
              {isRunning ? '透传服务运行中' : '透传服务已停止'}
            </span>
          </div>

          <Button
            onClick={handleToggle}
            variant={isRunning ? 'destructive' : 'default'}
            size="sm"
            className="flex items-center space-x-2"
          >
            {isRunning ? (
              <>
                <Square className="w-4 h-4" />
                <span>停止服务</span>
              </>
            ) : (
              <>
                <Play className="w-4 h-4" />
                <span>启动服务</span>
              </>
            )}
          </Button>
        </div>
      </div>

      {/* 配置面板 */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Settings className="w-5 h-5" />
            <span>透传配置</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            <div className="space-y-4">
              <div className="space-y-2">
                <label className="text-sm font-medium">输入协议</label>
                <Select
                  value={config.inputProtocol}
                  onValueChange={(value) => setConfig({ ...config, inputProtocol: value })}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="openai">OpenAI</SelectItem>
                    <SelectItem value="anthropic">Anthropic</SelectItem>
                    <SelectItem value="custom">Custom</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium">输出协议</label>
                <Select
                  value={config.outputProtocol}
                  onValueChange={(value) => setConfig({ ...config, outputProtocol: value })}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="openai">OpenAI</SelectItem>
                    <SelectItem value="anthropic">Anthropic</SelectItem>
                    <SelectItem value="custom">Custom</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="space-y-4">
              <div className="space-y-2">
                <label className="text-sm font-medium">目标URL</label>
                <Input
                  value={config.targetUrl}
                  onChange={(e) => setConfig({ ...config, targetUrl: e.target.value })}
                  placeholder="http://localhost:5506"
                />
              </div>
            </div>

            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <label className="text-sm font-medium">记录IO</label>
                <Switch
                  checked={config.recordEnabled}
                  onCheckedChange={(checked) => setConfig({ ...config, recordEnabled: checked })}
                />
              </div>

              <div className="flex items-center justify-between">
                <label className="text-sm font-medium">启用分析</label>
                <Switch
                  checked={config.analysisEnabled}
                  onCheckedChange={(checked) => setConfig({ ...config, analysisEnabled: checked })}
                />
              </div>

              <div className="flex items-center justify-between">
                <label className="text-sm font-medium">协议转换</label>
                <Switch
                  checked={config.transformEnabled}
                  onCheckedChange={(checked) => setConfig({ ...config, transformEnabled: checked })}
                />
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* 测试面板 */}
      <Card>
        <CardHeader>
          <CardTitle>协议测试</CardTitle>
          <CardDescription>
            发送测试请求并分析协议结构
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-6">
            {/* 请求配置 */}
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold">测试请求</h3>
                <div className="flex items-center space-x-2">
                  <Button
                    onClick={() => setShowRawData(!showRawData)}
                    variant="outline"
                    size="sm"
                    className="flex items-center space-x-2"
                  >
                    {showRawData ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                    <span>{showRawData ? '表单模式' : 'JSON模式'}</span>
                  </Button>

                  <Button
                    onClick={handleTestRequest}
                    disabled={isProcessing}
                    size="sm"
                    className="flex items-center space-x-2"
                  >
                    <Play className="w-4 h-4" />
                    <span>{isProcessing ? '处理中...' : '发送请求'}</span>
                  </Button>

                  <Button
                    onClick={handleExportTest}
                    variant="outline"
                    size="sm"
                    className="flex items-center space-x-2"
                  >
                    <Download className="w-4 h-4" />
                    <span>导出</span>
                  </Button>
                </div>
              </div>

              {showRawData ? (
                <div className="space-y-2">
                  <Textarea
                    value={JSON.stringify(testRequest, null, 2)}
                    onChange={(e) => {
                      try {
                        const parsed = JSON.parse(e.target.value);
                        setTestRequest(parsed);
                      } catch (error) {
                        // JSON格式错误，不更新状态
                      }
                    }}
                    rows={10}
                    className="font-mono text-sm"
                    placeholder="输入JSON格式的请求数据..."
                  />
                </div>
              ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <label className="text-sm font-medium">模型</label>
                    <Input
                      value={testRequest.model}
                      onChange={(e) => setTestRequest({ ...testRequest, model: e.target.value })}
                    />
                  </div>

                  <div className="space-y-2">
                    <label className="text-sm font-medium">温度</label>
                    <Input
                      type="number"
                      step="0.1"
                      value={testRequest.temperature}
                      onChange={(e) => setTestRequest({ ...testRequest, temperature: parseFloat(e.target.value) })}
                    />
                  </div>

                  <div className="space-y-2">
                    <label className="text-sm font-medium">最大Token数</label>
                    <Input
                      type="number"
                      value={testRequest.max_tokens}
                      onChange={(e) => setTestRequest({ ...testRequest, max_tokens: parseInt(e.target.value) })}
                    />
                  </div>
                </div>
              )}
            </div>

            {/* 请求分析 */}
            {requestAnalysis && (
              <div className="border rounded-lg p-4 space-y-3">
                <div className="flex items-center justify-between">
                  <h4 className="font-semibold">请求分析</h4>
                  <Button
                    onClick={() => handleCopyToClipboard(requestAnalysis)}
                    variant="outline"
                    size="sm"
                  >
                    <Copy className="w-4 h-4" />
                  </Button>
                </div>
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                  <div>
                    <span className="font-medium">字段数:</span> {requestAnalysis.totalFields}
                  </div>
                  <div>
                    <span className="font-medium">估算Token:</span> {requestAnalysis.estimatedTokens}
                  </div>
                  <div>
                    <span className="font-medium">数据大小:</span> {(requestAnalysis.dataSize / 1024).toFixed(2)}KB
                  </div>
                  <div>
                    <span className="font-medium">复杂度:</span>
                    <Badge variant="outline" className="ml-1">{requestAnalysis.complexity}</Badge>
                  </div>
                </div>
              </div>
            )}

            {/* 响应显示 */}
            {testResponse && (
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-semibold">测试响应</h3>
                  <Button
                    onClick={() => handleCopyToClipboard(testResponse)}
                    variant="outline"
                    size="sm"
                  >
                    <Copy className="w-4 h-4" />
                  </Button>
                </div>

                <div className="bg-gray-50 dark:bg-gray-800 p-4 rounded-md max-h-96 overflow-auto">
                  <pre className="text-sm font-mono">
                    {JSON.stringify(testResponse, null, 2)}
                  </pre>
                </div>

                {/* 响应分析 */}
                {responseAnalysis && (
                  <div className="border rounded-lg p-4 space-y-3">
                    <div className="flex items-center justify-between">
                      <h4 className="font-semibold">响应分析</h4>
                      <Button
                        onClick={() => handleCopyToClipboard(responseAnalysis)}
                        variant="outline"
                        size="sm"
                      >
                        <Copy className="w-4 h-4" />
                      </Button>
                    </div>
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                      <div>
                        <span className="font-medium">字段数:</span> {responseAnalysis.totalFields}
                      </div>
                      <div>
                        <span className="font-medium">估算Token:</span> {responseAnalysis.estimatedTokens}
                      </div>
                      <div>
                        <span className="font-medium">数据大小:</span> {(responseAnalysis.dataSize / 1024).toFixed(2)}KB
                      </div>
                      <div>
                        <span className="font-medium">复杂度:</span>
                        <Badge variant="outline" className="ml-1">{responseAnalysis.complexity}</Badge>
                      </div>
                    </div>
                    <div className="flex flex-wrap gap-2 mt-3">
                      {responseAnalysis.hasTools && (
                        <Badge variant="outline">包含工具</Badge>
                      )}
                      {responseAnalysis.hasStreaming && (
                        <Badge variant="outline">流式响应</Badge>
                      )}
                      <Badge variant="outline">类型: {responseAnalysis.contentType}</Badge>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

